import React, { useState } from "react";
import { useNavigate } from "react-router-dom"; // ÏàòÏ†ï: useNavigate Ï∂îÍ∞Ä
import { signup, login } from "../api/auth"; // ‚úÖ API Ìò∏Ï∂ú Ìï®Ïàò
import { handleLoginSuccess } from "../utils/authUtils"; // ‚úÖ Î°úÍ∑∏Ïù∏ ÏÑ±Í≥µ Ïãú ÌÜ†ÌÅ∞ Ï†ÄÏû•
import "../styles/Login.scss";
import FacebookIcon from "@mui/icons-material/Facebook";
import GoogleIcon from "@mui/icons-material/Google";
import TwitterIcon from "@mui/icons-material/Twitter";
import axios from "axios"; // ‚úÖ axios import Ï∂îÍ∞Ä


const LoginPage = () => {
	const [isSignUp, setIsSignUp] = useState(false);
	const [name, setName] = useState("");
	const [email, setEmail] = useState("");
	const [password, setPassword] = useState(""); // ‚úÖ Í∏∞Ï°¥Ïóê 30ÏúºÎ°ú ÏÑ§Ï†ïÎêú Ï¥àÍ∏∞Í∞íÏùÑ Îπà Î¨∏ÏûêÏó¥Î°ú Î≥ÄÍ≤Ω
	const [duration, setDuration] = useState(30); // üîπ Î°úÍ∑∏Ïù∏ Ïó∞Ïû• Í∏∞Î≥∏Í∞í: 30Î∂Ñ Ï∂îÍ∞Ä
	const navigate = useNavigate(); // ÏàòÏ†ï: navigate ÏÇ¨Ïö©

	const toggleForm = () => setIsSignUp((prev) => !prev);

	// üîπ ÌöåÏõêÍ∞ÄÏûÖ ÏöîÏ≤≠
	const handleSignUp = async (e) => {
		e.preventDefault();
		const response = await signup(name, email, password);

		if (response.success) {
			alert("ÌöåÏõêÍ∞ÄÏûÖ ÏÑ±Í≥µ! ÏûêÎèô Î°úÍ∑∏Ïù∏Îê©ÎãàÎã§.");
			await handleSignIn(e); // ‚úÖ ÌöåÏõêÍ∞ÄÏûÖ ÌõÑ ÏûêÎèô Î°úÍ∑∏Ïù∏
		} else {
			alert(response.message || "ÌöåÏõêÍ∞ÄÏûÖ Ïã§Ìå®");
		}
	};

	// üîπ Î°úÍ∑∏Ïù∏ ÏöîÏ≤≠
	const handleSignIn = async (e) => {
		e.preventDefault();
		const response = await login(email, password, duration); // ‚úÖ Î°úÍ∑∏Ïù∏ ÏöîÏ≤≠ Ïãú duration Í∞íÏùÑ Ï†ÑÎã¨

		// if (response.accessToken) {
		// 	localStorage.setItem("token", response.accessToken);
		// 	localStorage.setItem("refreshToken", response.refreshToken);
		if (response.accessToken) {
			handleLoginSuccess(response.accessToken, response.refreshToken);

			// ‚úÖ Î°úÍ∑∏Ïù∏Ìïú ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥ Í∞ÄÏ†∏Ïò§Í∏∞
			const userInfo = await fetchUserInfo();
			console.log(" Î°úÍ∑∏Ïù∏Ìïú ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥: ", userInfo); //ÎîîÎ≤ÑÍπÖ Î°úÍ∑∏
			if (userInfo && userInfo.id) {
				localStorage.setItem("user", JSON.stringify(userInfo));
				alert("Î°úÍ∑∏Ïù∏ ÏÑ±Í≥µ!");
				navigate(`/dashboard/${userInfo.id}`); // ÏàòÏ†ï: Î°úÍ∑∏Ïù∏ ÌõÑ /dashboard/{ÏÇ¨Ïö©Ïûê ID}Î°ú Ïù¥Îèô
			} else {
				alert("ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥Î•º Î∂àÎü¨Ïò§Îäî Îç∞ Ïã§Ìå®ÌñàÏäµÎãàÎã§.");
				navigate("/");
			}
		} else {
			alert(response.message || "Î°úÍ∑∏Ïù∏ Ïã§Ìå®");
		}
	};

	// ‚úÖ Î°úÍ∑∏Ïù∏Ìïú ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥ Í∞ÄÏ†∏Ïò§Í∏∞
	// const fetchUserInfo = async (token) => {
	// 	try {
	// 		const response = await axios.get("http://localhost:8082/api/user", {
	// 			headers: { Authorization: `Bearer ${token}` },
	// 		});
	// 		return response.data;
	// 	} catch (error) {
	// 		console.error("ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥Î•º Î∂àÎü¨Ïò§ÏßÄ Î™ªÌñàÏäµÎãàÎã§:", error);
	// 		return null;
	// 	}
	// };
	const fetchUserInfo = async () => {
		try {
			const token = localStorage.getItem("accessToken");
			if (!token) {
				console.error("‚ùå ÌÜ†ÌÅ∞Ïù¥ ÏóÜÏäµÎãàÎã§.");
				return null;
			}
	
			const response = await axios.get("http://localhost:8082/api/auth/me", {
				headers: { Authorization: `Bearer ${token}` },
			});
	
			console.log("üìå Î∞±ÏóîÎìú ÏùëÎãµ Îç∞Ïù¥ÌÑ∞:", response.data); // ‚úÖ Ï∂îÍ∞Ä
	
			if (!response.data.id) {
				console.error("‚ùå ÏÇ¨Ïö©Ïûê ID ÏóÜÏùå! Î∞±ÏóîÎìú ÏùëÎãµ ÏàòÏ†ï ÌïÑÏöî:", response.data);
				return null;
			}
	
			return response.data;
		} catch (error) {
			console.error("‚ùå ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥Î•º Î∂àÎü¨Ïò§ÏßÄ Î™ªÌñàÏäµÎãàÎã§:", error);
			return null;
		}
	};
	
	return (
		<div className="login-page">
			<div className="main">
				{/* ÌöåÏõêÍ∞ÄÏûÖ Ìèº */}
				<div className={`container a-container ${isSignUp ? "is-hidden" : ""}`}>
					<form className="form" onSubmit={handleSignUp}>
						<h2 className="form_title title">Create Account</h2>
						<div className="form__icons">
							<FacebookIcon className="form__icon" />
							<GoogleIcon className="form__icon" />
							<TwitterIcon className="form__icon" />
						</div>
						<span className="form__span">or use email for registration</span>
						<input className="form__input" type="text" placeholder="Name" value={name} onChange={(e) => setName(e.target.value)} required />
						<input className="form__input" type="email" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} required />
						<input className="form__input" type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} required />
						<button type="submit" className="form__button button submit">SIGN UP</button>
					</form>
				</div>

				{/* Î°úÍ∑∏Ïù∏ Ìèº */}
				<div className={`container b-container ${isSignUp ? "" : "is-hidden"}`}>
					<form className="form" onSubmit={handleSignIn}>
						<h2 className="form_title title">Sign in to Website</h2>
						<div className="form__icons">
							<FacebookIcon className="form__icon" />
							<GoogleIcon className="form__icon" />
