import React, { useEffect, useState } from "react";
import axios from "axios";
import { getAccessToken } from "../utils/authUtils";
import { over } from "stompjs";
import SockJS from "sockjs-client";


const InviteList = ({ refreshProjects }) => {
  const [invites, setInvites] = useState([]);
  const [stompClient, setStompClient] = useState(null);


  useEffect(() => {
    const fetchInvites = async () => {
      const token = getAccessToken();
      try {
        const response = await axios.get("http://localhost:8082/api/team/invites", {
          headers: { Authorization: `Bearer ${token}` },
        });
        console.log("üìå Î∞õÏùÄ Ï¥àÎåÄ Î™©Î°ù:", response.data);
        setInvites(response.data);
      } catch (error) {
        console.error("‚ùå Ï¥àÎåÄ Î™©Î°ù Î∂àÎü¨Ïò§Í∏∞ Ïã§Ìå®:", error);
      }
    };

    fetchInvites();

    // ‚úÖ WebSocket Ïó∞Í≤∞
    const socket = new SockJS("http://localhost:8082/ws");
    const stomp = over(socket);
    stomp.connect({}, () => {
      console.log("‚úÖ WebSocket Ïó∞Í≤∞Îê®");

      // ‚úÖ ÏÇ¨Ïö©Ïûê Ïù¥Î©îÏùºÏùÑ Í∏∞Î∞òÏúºÎ°ú Ï¥àÎåÄ ÏïåÎ¶º Íµ¨ÎèÖ
      const email = getAccessToken()?.split(".")[1]; // Ïù¥Î©îÏùº Ï∂îÏ∂ú (ÏòàÏãú)
      stomp.subscribe(`/topic/invites/${email}`, (message) => {
        console.log("üì© ÏÉàÎ°úÏö¥ Ï¥àÎåÄ ÏïåÎ¶º:", message.body);
        fetchInvites();
      });
    });

    setStompClient(stomp);

    return () => {
      if (stompClient) {
        stompClient.disconnect();
      }
    };
  }, []);

  // ‚úÖ Ï¥àÎåÄ ÏàòÎùΩ Ìï®Ïàò
  const acceptInvite = async (inviteId) => {
    const token = getAccessToken();
    try {
      await axios.post(`http://localhost:8082/api/team/invite/${inviteId}/accept`, null, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert("Ï¥àÎåÄÎ•º ÏàòÎùΩÌñàÏäµÎãàÎã§!");
      fetchInvites(); // ‚úÖ Ï¥àÎåÄ Î™©Î°ù Îã§Ïãú Î∂àÎü¨Ïò§Í∏∞
      refreshProjects(); // ‚úÖ ÌîÑÎ°úÏ†ùÌä∏ Î™©Î°ùÎèÑ ÏÉàÎ°úÍ≥†Ïπ® (Ï∂îÍ∞ÄÎêú ÌîÑÎ°úÏ†ùÌä∏Í∞Ä Î≥¥Ïù¥ÎèÑÎ°ù)
    } catch (error) {
      console.error("‚ùå Ï¥àÎåÄ ÏàòÎùΩ Ïã§Ìå®:", error);
    }
  };

  return (
    <div className="invite-container">
      <h2>Ï¥àÎåÄ Î™©Î°ù</h2>
      {invites.length === 0 ? <p>üì≠ Ï¥àÎåÄÍ∞Ä ÏóÜÏäµÎãàÎã§.</p> : null}
      {invites.map((invite) => (
        <div key={invite.id} className="invite-card">
          <p><strong>Ï¥àÎåÄÌïú ÏÇ¨Îûå:</strong> {invite.sender.username}</p>
          <p><strong>ÌîÑÎ°úÏ†ùÌä∏:</strong> {invite.project.name}</p>
          <button onClick={() => acceptInvite(invite.id)} className="accept-btn">‚úÖ ÏàòÎùΩ</button>
        </div>
      ))}
    </div>
  );
};

export default InviteList;
