import React, { useState, useEffect } from "react";
import Sidebar from "../components/Sidebar";
import GanttChart from "../components/GanttChart";
import Chatbox from "../components/Chatbox";
import Dashboard from "../components/Dashboard";
import TaskBoard from "../components/TaskBoard";
import "../styles/MainPage.css";
import axios from "axios";

const MainPage = () => {
    const [projects, setProjects] = useState([]); // Î°úÍ∑∏Ïù∏ ÌõÑ ÌîÑÎ°úÏ†ùÌä∏ ÏóÜÏùå

    useEffect(() => {
        setProjects([]); // Î°úÍ∑∏Ïù∏ ÌõÑ GanttChartÎäî ÎπÑÏñ¥ÏûàÎäî ÏÉÅÌÉú Ïú†ÏßÄ
    }, []);

    const [projectName, setProjectName] = useState(() => {
        return localStorage.getItem("projectName") || ""; // Ï†ÄÏû•Îêú ÌîÑÎ°úÏ†ùÌä∏ Ïù¥Î¶Ñ Î∂àÎü¨Ïò§Í∏∞
    });

    const [showModal, setShowModal] = useState(false); // ÌîÑÎ°úÏ†ùÌä∏ ÏÉùÏÑ± Î™®Îã¨ ÏÉÅÌÉú


        // ÌîÑÎ°úÏ†ùÌä∏ ÏÉùÏÑ± Ìï®Ïàò
        const handleCreateProject = () => {
            const name = prompt("ÏÉàÎ°úÏö¥ ÌîÑÎ°úÏ†ùÌä∏ Ïù¥Î¶ÑÏùÑ ÏûÖÎ†•ÌïòÏÑ∏Ïöî:");
            if (name) {
                setProjectName(name);
                localStorage.setItem("projectName", name); // ÌîÑÎ°úÏ†ùÌä∏ Ïù¥Î¶Ñ Ï†ÄÏû•
            }
        };

    return (
        <div className="main-page">
            <div className="content">
                {projects.length === 0 ? (
                    <div className="empty-gantt">
                        <p>ÌòÑÏû¨ ÌîÑÎ°úÏ†ùÌä∏Í∞Ä ÏóÜÏäµÎãàÎã§.</p>
                        <button className="create-project-btn" onClick={() => setShowModal(true)}>
                            ÏÉàÎ°úÏö¥ ÌîÑÎ°úÏ†ùÌä∏ ÏÉùÏÑ±
                        </button>
                    </div>
                ) : (
                    <GanttChart tasks={[]} />
                )}
                <TaskBoard />
                <Dashboard tasks={[]} />
            </div>
            <div className="chatbox-container">
                <Sidebar />
                <Chatbox />
            </div>

            {/* üîπ ÌîÑÎ°úÏ†ùÌä∏ ÏÉùÏÑ± Î™®Îã¨ */}
            {showModal && (
                <div className="modal">
                    <div className="modal-content">
                        <h2>ÏÉà ÌîÑÎ°úÏ†ùÌä∏ ÏÉùÏÑ±</h2>
                        <input 
                            type="text" 
                            placeholder="ÌîÑÎ°úÏ†ùÌä∏ Ïù¥Î¶Ñ" 
                            value={projectName}
                            onChange={(e) => setProjectName(e.target.value)}
                        />
                        <button onClick={handleCreateProject}>ÏÉùÏÑ±</button>
                        <button onClick={() => setShowModal(false)}>Ï∑®ÏÜå</button>
                    </div>
                </div>
            )}
        </div>
    );
};

export default MainPage;
