import React, { useState, useEffect } from "react";
import Sidebar from "../components/Sidebar";
import GanttChart from "../components/GanttChart";
import Chatbox from "../components/Chatbox";
import Dashboard from "../components/Dashboard";
import TaskBoard from "../components/TaskBoard";
import "../styles/MainPage.css";
import axios from "axios";
import { useNavigate, useParams } from "react-router-dom";
import { getAccessToken } from "../utils/authUtils";

const MainPage = () => {
    const [projects, setProjects] = useState([]); // ‚úÖ ÌîÑÎ°úÏ†ùÌä∏ Î™©Î°ù Ï†ÄÏû•
    const [selectedProject, setSelectedProject] = useState(null); // ‚úÖ ÏÑ†ÌÉùÌïú ÌîÑÎ°úÏ†ùÌä∏ Ï†ÄÏû•
    const [tasks, setTasks] = useState([]); // ‚úÖ ÏÑ†ÌÉùÌïú ÌîÑÎ°úÏ†ùÌä∏Ïùò ÏûëÏóÖ Î™©Î°ù
    const [showModal, setShowModal] = useState(false);
    const [projectName, setProjectName] = useState("");
    const [user, setUser] = useState(null);
    const { userId } = useParams();
    const navigate = useNavigate();

    // ‚úÖ Î°úÍ∑∏Ïù∏Ìïú ÏÇ¨Ïö©ÏûêÏùò ÌîÑÎ°úÏ†ùÌä∏ Î™©Î°ù Î∂àÎü¨Ïò§Í∏∞
    const fetchProjects = async () => {
        const token = getAccessToken();
        if (!token) {
            alert("Î°úÍ∑∏Ïù∏Ïù¥ ÌïÑÏöîÌï©ÎãàÎã§.");
            navigate("/login");
            return;
        }

        try {
            const response = await axios.get("http://localhost:8082/api/user/projects", {
                headers: {
                    Authorization: `Bearer ${token}`,
                    "Content-Type": "application/json",
                },
                withCredentials: true,
            });

            console.log("‚úÖ ÏÑúÎ≤ÑÏóêÏÑú Í∞ÄÏ†∏Ïò® ÌîÑÎ°úÏ†ùÌä∏ Î™©Î°ù:", response.data);
            if (response.data && response.data.length > 0) {
                const formattedProjects = response.data.map(project => ({
                    ...project,
                    name: project.name ? project.name : "üö® Ïù¥Î¶Ñ ÏóÜÏùå", // ‚úÖ Ïù¥Î¶ÑÏù¥ ÏóÜÏúºÎ©¥ Í∏∞Î≥∏Í∞í ÏÑ§Ï†ï
                }));
                setProjects(formattedProjects);
                handleProjectSelect(formattedProjects[0]); // ‚úÖ Ï≤´ Î≤àÏß∏ ÌîÑÎ°úÏ†ùÌä∏ ÏÑ†ÌÉù
            }
        } catch (error) {
            console.error("‚ùå ÌîÑÎ°úÏ†ùÌä∏ Î™©Î°ù Î∂àÎü¨Ïò§Í∏∞ Ïã§Ìå®:", error);
            if (error.response?.status === 401) {
                localStorage.removeItem("accessToken");
                sessionStorage.removeItem("accessToken");
                navigate("/login");
            }
        }
    };

    // Ï∂îÍ∞ÄÍ∞Ä: ÏÉàÎ°úÏö¥ Access Token Î∞úÍ∏â
    const refreshAccessToken = async () => {
        const refreshToken = localStorage.getItem("refreshToken");
        if (!refreshToken) {
            navigate("/login");
            return;
        }

        try {
            const response = await axios.post("http://localhost:8082/api/auth/refresh", {
                refreshToken,
            });

            localStorage.setItem("accessToken", response.data.accessToken);
            sessionStorage.setItem("accessToken", response.data.accessToken); // ‚úÖ Ï∂îÍ∞Ä: sessionStorageÏóêÎèÑ Ï†ÄÏû•
            console.log("‚úÖ ÏÉà Access Token Î∞úÍ∏â:", response.data.accessToken);
            return response.data.accessToken;
        } catch (error) {
            console.error("üö® ÌÜ†ÌÅ∞ Í∞±Ïã† Ïã§Ìå®, Îã§Ïãú Î°úÍ∑∏Ïù∏ ÌïÑÏöî:", error);
            navigate("/login");
        }
    };
    // ‚úÖ ÏÉàÎ°úÏö¥ ÌîÑÎ°úÏ†ùÌä∏ ÏÉùÏÑ±
    const handleCreateProject = async () => {
        const token = getAccessToken(); // sessionStorageÏóêÏÑúÎèÑ accessTokenÏùÑ Í∞ÄÏ†∏Ïò¨ Ïàò ÏûàÎèÑÎ°ù Î≥ÄÍ≤Ω

        if (!token) {
            alert("Î°úÍ∑∏Ïù∏Ïù¥ ÌïÑÏöîÌï©ÎãàÎã§.");
            navigate("/login");
            return;
        }

        try {

            const newProjectData = {
                name: `ÏÉà ÌîÑÎ°úÏ†ùÌä∏ ${projects.length + 1}`,
                description: "ÏÉàÎ°úÏö¥ ÌîÑÎ°úÏ†ùÌä∏ÏûÖÎãàÎã§.",
                startDate: new Date().toISOString().split("T")[0],
            };

            const response = await axios.post(
                "http://localhost:8082/api/user/projects", newProjectData, {
                // { name: projectName, startDate: new Date().toISOString().split("T")[0] },
                headers: {
                    Authorization: `Bearer ${token}`,
                    "Content-Type": "application/json",
                },
                withCredentials: true,
            });

            console.log("‚úÖ ÏÉà ÌîÑÎ°úÏ†ùÌä∏ ÏÉùÏÑ± ÏùëÎãµ:", response.data);

            // ‚úÖ ÌîÑÎ°úÏ†ùÌä∏ ÏÉùÏÑ± ÌõÑ Task ÌéòÏù¥ÏßÄÎ°ú Ïù¥Îèô
            navigate(`/task?projectId=${response.data.id}`);

            setProjects((prevProjects) => [
                ...prevProjects,
                {
                    ...response.data,
                    name: response.data.name ? response.data.name : "üö® Ïù¥Î¶Ñ ÏóÜÏùå", // ‚úÖ Ïù¥Î¶Ñ Í∏∞Î≥∏Í∞í ÏÑ§Ï†ï
                }
            ]);

            handleProjectSelect(response.data);
        } catch (error) {
            console.error("‚ùå ÌîÑÎ°úÏ†ùÌä∏ ÏÉùÏÑ± Ïã§Ìå®:", error);
            alert("ÌîÑÎ°úÏ†ùÌä∏ ÏÉùÏÑ±Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.");
        }
    };

    // ‚úÖ ÏÑ†ÌÉùÌïú ÌîÑÎ°úÏ†ùÌä∏Ïùò ÏûëÏóÖ(Task) Î™©Î°ù Î∂àÎü¨Ïò§Í∏∞
    const fetchTasks = async (projectId) => {
        const token = getAccessToken();
        if (!token) {
            alert("Î°úÍ∑∏Ïù∏Ïù¥ ÌïÑÏöîÌï©ÎãàÎã§.");
            navigate("/login");
            return;
        }

        try {
            const response = await axios.get(`http://localhost:8082/api/user/projects/${projectId}/tasks`, {
                headers: {
                    Authorization: `Bearer ${token}`,
                    "Content-Type": "application/json",
                },
                withCredentials: true,
            });

            console.log(`‚úÖ ÌîÑÎ°úÏ†ùÌä∏ ${projectId}Ïùò ÏûëÏóÖ Î™©Î°ù:`, response.data);
            setTasks(response.data);
        } catch (error) {
            console.error(`‚ùå ÌîÑÎ°úÏ†ùÌä∏ ${projectId}Ïùò ÏûëÏóÖ Î™©Î°ù Î∂àÎü¨Ïò§Í∏∞ Ïã§Ìå®:`, error);
        }
    };
    // ‚úÖ ÌîÑÎ°úÏ†ùÌä∏ ÏÑ†ÌÉù Ïãú Ï≤òÎ¶¨ Ìï®Ïàò
    const handleProjectSelect = (project) => {
        setSelectedProject(project);
        fetchTasks(project.id);
        console.log("üîç ÏÑ†ÌÉùÎêú ÌîÑÎ°úÏ†ùÌä∏:", project);
    };


    useEffect(() => {
        fetchProjects();
    }, []);


    return (
        <div className="main-page">
            <div className="content">
                {/* ‚úÖ ÌîÑÎ°úÏ†ùÌä∏ Î™©Î°ù ÌëúÏãú */}
                <div className="project-list">
                    <h2>üìÇ ÌîÑÎ°úÏ†ùÌä∏ Î™©Î°ù</h2>
                    {projects.length === 0 ? (
                        <p>ÌòÑÏû¨ ÌîÑÎ°úÏ†ùÌä∏Í∞Ä ÏóÜÏäµÎãàÎã§.</p>
                    ) : (
                        <ul className="project-list-container">
                            {projects.map((project) => (
                                <li key={project.id}>
                                    <button
                                        className={`project-btn ${selectedProject?.id === project.id ? "active" : ""}`}
                                        onClick={() =>
                                            handleProjectSelect(project)
                                            // {setSelectedProject(project);
                                            // localStorage.setItem("selectedProjectId", project.id)}
                                        }
                                    >
                                        {/* ÏàòÏ†ï: ÌîÑÎ°úÏ†ùÌä∏ Ïù¥Î¶ÑÏù¥ ÏóÜÏùÑ Í≤ΩÏö∞ ÎåÄÎπÑ */}
                                        {project?.name || "üö® Ïù¥Î¶Ñ ÏóÜÏùå"}
                                    </button>
                                </li>
                            ))}
                        </ul>
                    )}
                    <button className="create-project-btn" onClick={() => setShowModal(true)}>
                        ÏÉàÎ°úÏö¥ ÌîÑÎ°úÏ†ùÌä∏ ÏÉùÏÑ±
                    </button>
                </div>

                {/* ‚úÖ ÏÑ†ÌÉùÌïú ÌîÑÎ°úÏ†ùÌä∏Ïùò Í∞ÑÌä∏ Ï∞®Ìä∏ ÌëúÏãú */}
                {selectedProject ? (
                    <div className="project-details">
                        <h2>üìä {selectedProject.name} - Gantt Chart</h2>
                        <GanttChart project={selectedProject} tasks={tasks} />
                    </div>
                ) : (
                    <p className="no-project-selected">üìå ÌîÑÎ°úÏ†ùÌä∏Î•º ÏÑ†ÌÉùÌï¥Ï£ºÏÑ∏Ïöî.</p>
                )}

                <TaskBoard />
                <Dashboard tasks={[]} />
            </div>
            <div className="chatbox-container">
                <Sidebar />
                <Chatbox />
            </div>

            {/* üîπ ÌîÑÎ°úÏ†ùÌä∏ ÏÉùÏÑ± Î™®Îã¨ */}
            {showModal && (
                <div className="modal">
                    <div className="modal-content">
                        <h2>ÏÉà ÌîÑÎ°úÏ†ùÌä∏ ÏÉùÏÑ±</h2>
                        <input
                            type="text"
                            placeholder="ÌîÑÎ°úÏ†ùÌä∏ Ïù¥Î¶Ñ"
                            value={projectName}
                            onChange={(e) => setProjectName(e.target.value)}
                        />
                        <button onClick={handleCreateProject}>ÏÉùÏÑ±</button>
                        <button onClick={() => setShowModal(false)}>Ï∑®ÏÜå</button>
                    </div>
                </div>
            )}
        </div>
    );
};

export default MainPage;
