import axios from "axios";

const API_BASE_URL = "http://localhost:8082";

export const registerUser = async (userData) => {
  try {
    const response = await axios.post(`${API_BASE_URL}/register`, userData);
    return response.data;
  } catch (error) {
    throw error.response?.data || "ÌöåÏõêÍ∞ÄÏûÖ Ïã§Ìå®";
  }
};

export const loginUser = async (loginData) => {
  try {
    const response = await axios.post(`${API_BASE_URL}/login`, loginData, {
      withCredentials: true, // ‚úÖ Ïø†ÌÇ§ Í∏∞Î∞ò Ïù∏Ï¶ù ÏÇ¨Ïö©
    });
    return response.data;
  } catch (error) {
    throw error.response?.data || "Î°úÍ∑∏Ïù∏ Ïã§Ìå®";
  }
};

/** Î°úÍ∑∏Ïù∏ ÏÉÅÌÉúÎ•º ÏÑúÎ≤ÑÏóêÏÑú ÏßÅÏ†ë Ï°∞ÌöåÌïòÎäî Î∞©Ïãù */
export const getUserInfo = async () => {
  try {
    // ‚úÖ Ïø†ÌÇ§ÏóêÏÑú accessToken Ï∂îÏ∂ú
    const cookies = document.cookie.split("; ").reduce((acc, cookie) => {
      const [key, value] = cookie.split("=");
      acc[key] = value;
      return acc;
    }, {});

    if (!cookies.accessToken) {
      console.warn("‚õîÔ∏è accessToken ÏóÜÏùå: ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥ ÏöîÏ≤≠ Ïïà Ìï®");
      return null;
    }

    // ‚úÖ Authorization Ìó§ÎçîÏóê JWT Ìè¨Ìï®
    const response = await axios.get(`${API_BASE_URL}/api/user`, {
      withCredentials: true, 
      headers: {
        Authorization: `Bearer ${cookies.accessToken}`
      }
    });
    return response.data; // ‚úÖ Î°úÍ∑∏Ïù∏Îêú ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥ Î∞òÌôò
  } catch (error) {
    if (error.response && error.response.status === 403) {
      console.warn("‚õîÔ∏è Î°úÍ∑∏Ïù∏ÎêòÏßÄ ÏïäÏùå: ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥ ÏöîÏ≤≠ÏùÑ Ï§ëÎã®Ìï©ÎãàÎã§.");
      return null;
    }
    console.error("üî¥ ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥ Ï°∞Ìöå Ïã§Ìå®:", error.response?.data || error);
    return null;
  }

};



