export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);

  // ë¡œê·¸ì¸ API ìš”ì²­
  const login = async (username, password) => {
    try {
      const response = await fetch("http://localhost:8080/api/users/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ username, password }),
      });

      if (!response.ok) throw new Error("ë¡œê·¸ì¸ ì‹¤íŒ¨");

      const data = await response.json();
      localStorage.setItem("token", data.token);
      setUser({ username: data.username });

      // ðŸš€ íŽ˜ì´ì§€ ìƒˆë¡œê³ ì¹¨í•˜ë©´ì„œ /dashboardë¡œ ì´ë™
      window.location.href = "/dashboard";
    } catch (error) {
      alert("ë¡œê·¸ì¸ ì‹¤íŒ¨");
    }
  };

  // ë¡œê·¸ì•„ì›ƒ ì²˜ë¦¬
  const logout = () => {
    localStorage.removeItem("token");
    setUser(null);

    // ðŸš€ ë¡œê·¸ì•„ì›ƒ í›„ ë©”ì¸ íŽ˜ì´ì§€ë¡œ ì´ë™í•˜ë©´ì„œ ìƒˆë¡œê³ ì¹¨
    window.location.href = "/";
  };

  // íŽ˜ì´ì§€ ìƒˆë¡œê³ ì¹¨ ì‹œ ë¡œê·¸ì¸ ìƒíƒœ ìœ ì§€
  useEffect(() => {
    const token = localStorage.getItem("token");
    if (token) {
      fetch("http://localhost:8080/api/users/me", {
        headers: { Authorization: `Bearer ${token}` },
      })
        .then((res) => res.json())
        .then((data) => setUser({ username: data.username }))
        .catch(() => logout());
    }
  }, []);

  return (
    <AuthContext.Provider value={{ user, login, logout }}>
      {children}
    </AuthContext.Provider>
  );
};
