import React, { useState, useEffect } from "react";
import Sidebar from "../components/Sidebar";
import GanttChart from "../components/GanttChart";
import Chatbox from "../components/Chatbox";
import Dashboard from "../components/Dashboard";
import TaskBoard from "../components/TaskBoard";
import "../styles/MainPage.css";
import axios from "axios";

const MainPage = () => {
    const [projects, setProjects] = useState([]); // Î°úÍ∑∏Ïù∏ ÌõÑ ÌîÑÎ°úÏ†ùÌä∏ ÏóÜÏùå
    const [selectedProject, setSelectedProject] = useState(null); // ‚úÖ ÌòÑÏû¨ ÏÑ†ÌÉùÎêú ÌîÑÎ°úÏ†ùÌä∏

    const [showModal, setShowModal] = useState(false);
    const [projectName, setProjectName] = useState("");

    useEffect(() => {
        setProjects([]); // Î°úÍ∑∏Ïù∏ ÌõÑ GanttChartÎäî ÎπÑÏñ¥ÏûàÎäî ÏÉÅÌÉú Ïú†ÏßÄ
    }, []);

    const handleCreateProject = async () => {
        const token = localStorage.getItem("token");

        if (!token) {
            alert("Î°úÍ∑∏Ïù∏Ïù¥ ÌïÑÏöîÌï©ÎãàÎã§.");
            return;
        }

        try {
            const response = await axios.post(
                "http://localhost:8082/api/user/projects", // ‚úÖ Î∞±ÏóîÎìú URLÍ≥º ÎèôÏùºÌïòÍ≤å ÏàòÏ†ï
                { name: projectName, startDate: new Date().toISOString().split("T")[0] },
                {
                    headers: {
                        Authorization: `Bearer ${token}`, // üîπ Ìó§ÎçîÏóê JWT ÌÜ†ÌÅ∞ Ìè¨Ìï®
                        "Content-Type": "application/json",
                    },
                }
            );

            console.log("‚úÖ ÌîÑÎ°úÏ†ùÌä∏ ÏÉùÏÑ± ÏÑ±Í≥µ:", response.data);
            setProjects([...projects, response.data]);
        } catch (error) {
            console.error("‚ùå ÌîÑÎ°úÏ†ùÌä∏ ÏÉùÏÑ± Ïã§Ìå®:", error);
            alert("ÌîÑÎ°úÏ†ùÌä∏ ÏÉùÏÑ±Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.");
        }
    };

    return (
        <div className="main-page">
            <div className="content">
                {projects.length === 0 ? (
                    <div className="empty-gantt">
                        <p>ÌòÑÏû¨ ÌîÑÎ°úÏ†ùÌä∏Í∞Ä ÏóÜÏäµÎãàÎã§.</p>
                        <button className="create-project-btn" onClick={() => setShowModal(true)}>
                            ÏÉàÎ°úÏö¥ ÌîÑÎ°úÏ†ùÌä∏ ÏÉùÏÑ±
                        </button>
                    </div>
                ) : (
                    <GanttChart tasks={[]} />
                )}
                <TaskBoard />
                <Dashboard tasks={[]} />
            </div>
            <div className="chatbox-container">
                <Sidebar />
                <Chatbox />
            </div>

            {/* üîπ ÌîÑÎ°úÏ†ùÌä∏ ÏÉùÏÑ± Î™®Îã¨ */}
            {showModal && (
                <div className="modal">
                    <div className="modal-content">
                        <h2>ÏÉà ÌîÑÎ°úÏ†ùÌä∏ ÏÉùÏÑ±</h2>
                        <input 
                            type="text" 
                            placeholder="ÌîÑÎ°úÏ†ùÌä∏ Ïù¥Î¶Ñ" 
                            value={projectName}
                            onChange={(e) => setProjectName(e.target.value)}
                        />
                        <button onClick={handleCreateProject}>ÏÉùÏÑ±</button>
                        <button onClick={() => setShowModal(false)}>Ï∑®ÏÜå</button>
                    </div>
                </div>
            )}
        </div>
    );
};

export default MainPage;
