const TaskPage = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const queryParams = new URLSearchParams(location.search);
  const projectId = queryParams.get("projectId");

  const [project, setProject] = useState(null); // ‚úÖ ÌîÑÎ°úÏ†ùÌä∏ Ï†ïÎ≥¥ ÏÉÅÌÉú 
  const [tasks, setTasks] = useState([]);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [editTask, setEditTask] = useState(null); // ÏàòÏ†ïÌï† Task Ï†ÄÏû•
  const [selectedTask, setSelectedTask] = useState(null); // Task ÏÉÅÏÑ∏ Î≥¥Í∏∞Ïö© ÏÉÅÌÉú

  // ‚úÖ ÌîÑÎ°úÏ†ùÌä∏ Ï†ïÎ≥¥ Î∂àÎü¨Ïò§Í∏∞
  const fetchProject = async () => {
    if (!projectId) return;

    const token = getAccessToken();
    if (!token) {
      alert("Î°úÍ∑∏Ïù∏Ïù¥ ÌïÑÏöîÌï©ÎãàÎã§.");
      navigate("/login");
      return;
    }

    try {
      const response = await axios.get(`http://localhost:8082/api/user/projects/${projectId}`, {
        headers: {
          Authorization: `Bearer ${token}`,
          "Content-Type": "application/json",
        },
        withCredentials: true,
      });

      setProject(response.data); // ‚úÖ ÌîÑÎ°úÏ†ùÌä∏ ÏÉÅÌÉú ÏóÖÎç∞Ïù¥Ìä∏
    } catch (error) {
      console.error("‚ùå ÌîÑÎ°úÏ†ùÌä∏ Ï†ïÎ≥¥ Î∂àÎü¨Ïò§Í∏∞ Ïã§Ìå®:", error);
    }
  };

  // ‚úÖ Task Î™©Î°ù Î∂àÎü¨Ïò§Í∏∞
  const fetchTasks = async () => {
    if (!projectId) return;

    const token = getAccessToken();
    if (!token) {
      alert("Î°úÍ∑∏Ïù∏Ïù¥ ÌïÑÏöîÌï©ÎãàÎã§.");
      navigate("/login");
      return;
    }

    try {
      const response = await axios.get(`http://localhost:8082/api/user/tasks?projectId=${projectId}`, {
        headers: {
          Authorization: `Bearer ${token}`,
          "Content-Type": "application/json",
        },
        withCredentials: true,
      });

      setTasks(response.data);
    } catch (error) {
      console.error("‚ùå Task Î™©Î°ù Î∂àÎü¨Ïò§Í∏∞ Ïã§Ìå®:", error);
    }
  };

  // ‚úÖ Task Ï∂îÍ∞Ä ÌõÑ Î™©Î°ù Í∞±Ïã†
  const refreshTasks = () => {
    fetchTasks();
  };

  // ‚úÖ Task ÏÇ≠Ï†ú Ìï®Ïàò 
  const handleDeleteTask = async (taskId) => {
    const token = getAccessToken();
    if (!token) {
      alert("Î°úÍ∑∏Ïù∏Ïù¥ ÌïÑÏöîÌï©ÎãàÎã§.");
      return;
    }

    if (!window.confirm("Ï†ïÎßêÎ°ú Ïù¥ ÏóÖÎ¨¥Î•º ÏÇ≠Ï†úÌïòÏãúÍ≤†ÏäµÎãàÍπå?")) return;

    try {
      await axios.delete(`http://localhost:8082/api/user/tasks/${taskId}`, {
        headers: {
          Authorization: `Bearer ${token}`,
          "Content-Type": "application/json",
        },
        withCredentials: true,
      });

      alert("ÏóÖÎ¨¥Í∞Ä ÏÑ±Í≥µÏ†ÅÏúºÎ°ú ÏÇ≠Ï†úÎêòÏóàÏäµÎãàÎã§.");
      refreshTasks(); // ‚úÖ ÏÇ≠Ï†ú ÌõÑ Î™©Î°ù ÏÉàÎ°úÍ≥†Ïπ®
    } catch (error) {
      console.error("‚ùå Task ÏÇ≠Ï†ú Ïã§Ìå®:", error);
      alert("ÏóÖÎ¨¥ ÏÇ≠Ï†úÏóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.");
    }
  };

  // ‚úÖ ÌîÑÎ°úÏ†ùÌä∏ Î∞è Task Î™©Î°ù Î∂àÎü¨Ïò§Í∏∞
  useEffect(() => {
    if (projectId) {
      fetchProject(); // ‚úÖ ÌîÑÎ°úÏ†ùÌä∏ Ï†ïÎ≥¥ Í∞ÄÏ†∏Ïò§Í∏∞
      fetchTasks(); // ‚úÖ Task Î™©Î°ù Í∞ÄÏ†∏Ïò§Í∏∞
    }
  }, [projectId]);

