import React, { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom"; // ‚úÖ useNavigate Ï∂îÍ∞Ä
import axios from "axios";
import { getAccessToken } from "../utils/authUtils"; // ‚úÖ Î°úÍ∑∏Ïù∏ ÏÉÅÌÉú ÌôïÏù∏ÏùÑ ÏúÑÌïú accessToken Í∞ÄÏ†∏Ïò§Í∏∞
import "../styles/Navbar.css";

const Navbar = () => {
  const [invitations, setInvitations] = useState([]);
  const [showPopup, setShowPopup] = useState(false);
  const [user, setUser] = useState(null);
  const navigate = useNavigate(); // ‚úÖ ÌéòÏù¥ÏßÄ Ïù¥ÎèôÏùÑ ÏúÑÌïú useNavigate Ï∂îÍ∞Ä

  // ‚úÖ ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥ Î∂àÎü¨Ïò§Í∏∞
  const fetchUserInfo = async () => {
    try {
      const token = getAccessToken(); // ‚úÖ ÏàòÏ†ï: sessionStorageÏóêÏÑúÎèÑ Í∞ÄÏ†∏Ïò¨ Ïàò ÏûàÎèÑÎ°ù Î≥ÄÍ≤Ω
      if (!token) {
        console.error("üö® JWT ÌÜ†ÌÅ∞Ïù¥ ÏóÜÏäµÎãàÎã§! Î°úÍ∑∏Ïù∏Ïù¥ ÌïÑÏöîÌï©ÎãàÎã§.");
        return;
      }

      const response = await axios.get("http://localhost:8082/api/auth/me", {
        headers: {
          Authorization: `Bearer ${token}`,
          "Content-Type": "application/json",
        },
        withCredentials: true,
      });

      localStorage.setItem("user", JSON.stringify(response.data));
      setUser(response.data);
    } catch (error) {
      console.error("‚ùå ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥ Î∂àÎü¨Ïò§Í∏∞ Ïã§Ìå®:", error);
    }
  };

  // ‚úÖ Ï¥àÎåÄ Î™©Î°ù Î∂àÎü¨Ïò§Í∏∞
  const fetchInvitations = async () => {
    try {
      const token = getAccessToken(); // ‚úÖ ÏàòÏ†ï: token Í∞ÄÏ†∏Ïò§Îäî Î∞©Ïãù ÌÜµÏùº
      if (!token) return;

      const response = await axios.get("http://localhost:8082/api/team/invitations", {
        headers: { Authorization: `Bearer ${token}` },
      });

      setInvitations(response.data);
      if (response.data.length > 0) setShowPopup(true);
    } catch (error) {
      console.error("Ï¥àÎåÄ Î™©Î°ù Î∂àÎü¨Ïò§Í∏∞ Ïã§Ìå®:", error);
    }
  };

  // ‚úÖ Ï¥àÎåÄ ÏàòÎùΩÌïòÍ∏∞
  const acceptInvite = async (inviteId) => {
    try {
      const token = getAccessToken(); // ‚úÖ ÏàòÏ†ï: token Í∞ÄÏ†∏Ïò§Îäî Î∞©Ïãù ÌÜµÏùº
      await axios.post(
        `http://localhost:8082/api/invites/${inviteId}/accept`,
        {},
        { headers: { Authorization: `Bearer ${token}` } }
      );

      alert("‚úÖ Ï¥àÎåÄÎ•º ÏàòÎùΩÌñàÏäµÎãàÎã§!");
      fetchInvitations(); // ÏÉà Ï¥àÎåÄ Î™©Î°ù Îã§Ïãú Î∂àÎü¨Ïò§Í∏∞
    } catch (error) {
      console.error("‚ùå Ï¥àÎåÄ ÏàòÎùΩ Ïã§Ìå®:", error);
    }
  };

  // ‚úÖ Î°úÍ∑∏ÏïÑÏõÉ
  const handleLogout = async () => {
    try {
      const token = getAccessToken(); // ‚úÖ ÏàòÏ†ï: sessionStorageÏóêÏÑúÎèÑ Í∞ÄÏ†∏Ïò¨ Ïàò ÏûàÎèÑÎ°ù Î≥ÄÍ≤Ω
      if (!token) {
        console.error("üö® JWT ÌÜ†ÌÅ∞Ïù¥ ÏóÜÏäµÎãàÎã§! Î°úÍ∑∏ÏïÑÏõÉ ÏöîÏ≤≠ÏùÑ Ìï† Ïàò ÏóÜÏäµÎãàÎã§.");
        return; 
      }

      await axios.post("http://localhost:8082/api/auth/logout", {}, {
        headers: { Authorization: `Bearer ${token}` }, 
        withCredentials: true
      });

      // ‚úÖ Ïø†ÌÇ§ ÏÇ≠Ï†ú (Î∏åÎùºÏö∞Ï†ÄÏóêÏÑú Í∞ïÏ†ú ÏÇ≠Ï†ú)
      document.cookie = "accessToken=; path=/; expires=Thu, 01 Jan 1970 00:00:00 UTC;";
      document.cookie = "refreshToken=; path=/; expires=Thu, 01 Jan 1970 00:00:00 UTC;";
      
      // ‚úÖ Î°úÏª¨Ïä§ÌÜ†Î¶¨ÏßÄ Î∞è ÏÑ∏ÏÖòÏä§ÌÜ†Î¶¨ÏßÄ ÏÇ≠Ï†ú
      localStorage.removeItem("accessToken");
      localStorage.removeItem("refreshToken");
      localStorage.removeItem("user");
      sessionStorage.removeItem("accessToken"); // ‚úÖ Ï∂îÍ∞Ä: sessionStorageÏóêÏÑúÎèÑ ÏÇ≠Ï†ú
      sessionStorage.removeItem("refreshToken"); // ‚úÖ Ï∂îÍ∞Ä: sessionStorageÏóêÏÑúÎèÑ ÏÇ≠Ï†ú

      setUser(null); // ‚úÖ ÏÉÅÌÉú ÏóÖÎç∞Ïù¥Ìä∏
      alert("Î°úÍ∑∏ÏïÑÏõÉ ÎêòÏóàÏäµÎãàÎã§.");
      navigate("/"); // ‚úÖ Î°úÍ∑∏Ïù∏ ÌéòÏù¥ÏßÄÎ°ú Ïù¥Îèô
    } catch (error) {
      console.error("Î°úÍ∑∏ÏïÑÏõÉ Ïã§Ìå®:", error);
    }
  };

  // ‚úÖ Î°úÍ∑∏Ïù∏ ÏÉÅÌÉú ÌôïÏù∏ ÌõÑ user ÏÉÅÌÉú ÏóÖÎç∞Ïù¥Ìä∏
  useEffect(() => {
    fetchUserInfo();
  }, []);

  return (
    <nav className="navbar">
      <div className="navbar-logo">TeamVerse</div>

      {/* ‚úÖ Ï¥àÎåÄ ÌåùÏóÖ */}
      {showPopup && (
        <div className="invitation-popup">
          <h2>Ï¥àÎåÄ ÏïåÎ¶º</h2>
          <ul>
            {invitations.map((invite) => (
              <li key={invite.id}>
                <p>{invite.email}ÎãòÏù¥ ÌåÄ Ï¥àÎåÄÎ•º Î≥¥ÎÉàÏäµÎãàÎã§.</p>
                <button className="accept-btn" onClick={() => acceptInvite(invite.id)}>
                  ÏàòÎùΩ
                </button>
              </li>
            ))}
          </ul>
          <button className="close-btn" onClick={() => setShowPopup(false)}>Îã´Í∏∞</button>
        </div>
      )}

      <div className="navbar-links">
        <Link to="/">Dashboard</Link>
        <Link to="/statistics">Statistics</Link>
        <Link to="/team-status">Team</Link>
        <Link to="/settings">Settings</Link>
        {user ? (
          <Link
            to="/login"
            onClick={(e) => {
              e.preventDefault(); 
              handleLogout(); 
            }}
          >
            Logout
          </Link>
        ) : (
          <Link to="/login">Login</Link>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
