import React, { useState, useEffect } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import axios from "axios";
import { getAccessToken } from "../utils/authUtils";
import TaskModal from "../components/TaskModal";
import TaskDetailModal from "../components/TaskDetailModal"; // Task ÏÉÅÏÑ∏ Î≥¥Í∏∞ Î™®Îã¨
import GanttChart from "../components/GanttChart"; // Í∞ÑÌä∏ Ï∞®Ìä∏ ÏûÑÌè¨Ìä∏
import "../styles/TaskPage.css"; //ÏÉàÎ°úÏö¥ CSS Ïä§ÌÉÄÏùº Ï†ÅÏö©

const TaskPage = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const queryParams = new URLSearchParams(location.search);
  const projectId = queryParams.get("projectId");

  const [project, setProject] = useState(null); // ‚úÖ ÌîÑÎ°úÏ†ùÌä∏ Ï†ïÎ≥¥ ÏÉÅÌÉú 
  const [tasks, setTasks] = useState([]);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [editTask, setEditTask] = useState(null); // ÏàòÏ†ïÌï† Task Ï†ÄÏû•
  const [selectedTask, setSelectedTask] = useState(null); // Task ÏÉÅÏÑ∏ Î≥¥Í∏∞Ïö© ÏÉÅÌÉú

  // ‚úÖ ÌîÑÎ°úÏ†ùÌä∏ Ï†ïÎ≥¥ Î∂àÎü¨Ïò§Í∏∞
  const fetchProject = async () => {
    if (!projectId) return;

    const token = getAccessToken();
    if (!token) {
      alert("Î°úÍ∑∏Ïù∏Ïù¥ ÌïÑÏöîÌï©ÎãàÎã§.");
      navigate("/login");
      return;
    }

    try {
      const response = await axios.get(`http://localhost:8082/api/user/projects/${projectId}`, {
        headers: {
          Authorization: `Bearer ${token}`,
          "Content-Type": "application/json",
        },
        withCredentials: true,
      });

      setProject(response.data); // ‚úÖ ÌîÑÎ°úÏ†ùÌä∏ ÏÉÅÌÉú ÏóÖÎç∞Ïù¥Ìä∏
    } catch (error) {
      console.error("‚ùå ÌîÑÎ°úÏ†ùÌä∏ Ï†ïÎ≥¥ Î∂àÎü¨Ïò§Í∏∞ Ïã§Ìå®:", error);
    }
  };

  // ‚úÖ Task Î™©Î°ù Î∂àÎü¨Ïò§Í∏∞
  const fetchTasks = async () => {
    if (!projectId) return;

    const token = getAccessToken();
    if (!token) {
      alert("Î°úÍ∑∏Ïù∏Ïù¥ ÌïÑÏöîÌï©ÎãàÎã§.");
      navigate("/login");
      return;
    }

    try {
      const response = await axios.get(`http://localhost:8082/api/user/tasks?projectId=${projectId}`, {
        headers: {
          Authorization: `Bearer ${token}`,
          "Content-Type": "application/json",
        },
        withCredentials: true,
      });

      setTasks(response.data);

      // ‚úÖ formattedTasks Î≥ÄÌôò (tasks Î∞∞Ïó¥ÏùÑ Í∞ÄÍ≥µ)
    const formattedTasks = response.data.map((task) => ({
      id: task.id,
      name: task.name,
      start: task.startDate ? new Date(task.startDate + "T00:00:00") : null, // ‚úÖ ÎÇ†Ïßú Î≥ÄÌôò
      end: task.dueDate ? new Date(task.dueDate + "T23:59:59") : null, // ‚úÖ ÎßàÍ∞êÏùº Î≥ÄÌôò
      color: task.color || "#ff99a5",
    }));
    console.log("üìå Î≥ÄÌôòÎêú Task Îç∞Ïù¥ÌÑ∞:", formattedTasks);

    } catch (error) {
      console.error("‚ùå Task Î™©Î°ù Î∂àÎü¨Ïò§Í∏∞ Ïã§Ìå®:", error);
    }
  };

  // ‚úÖ Task Ï∂îÍ∞Ä ÌõÑ Î™©Î°ù Í∞±Ïã†
  const refreshTasks = () => {
    fetchTasks();
  };

  // ‚úÖ Task ÏÇ≠Ï†ú Ìï®Ïàò 
  const handleDeleteTask = async (taskId) => {
    const token = getAccessToken();
    if (!token) {
      alert("Î°úÍ∑∏Ïù∏Ïù¥ ÌïÑÏöîÌï©ÎãàÎã§.");
      return;
    }

    if (!window.confirm("Ï†ïÎßêÎ°ú Ïù¥ ÏóÖÎ¨¥Î•º ÏÇ≠Ï†úÌïòÏãúÍ≤†ÏäµÎãàÍπå?")) return;

    try {
      await axios.delete(`http://localhost:8082/api/user/tasks/${taskId}`, {
        headers: {
          Authorization: `Bearer ${token}`,
          "Content-Type": "application/json",
        },
        withCredentials: true,
      });

      alert("ÏóÖÎ¨¥Í∞Ä ÏÑ±Í≥µÏ†ÅÏúºÎ°ú ÏÇ≠Ï†úÎêòÏóàÏäµÎãàÎã§.");
      refreshTasks(); // ‚úÖ ÏÇ≠Ï†ú ÌõÑ Î™©Î°ù ÏÉàÎ°úÍ≥†Ïπ®
    } catch (error) {
      console.error("‚ùå Task ÏÇ≠Ï†ú Ïã§Ìå®:", error);
      alert("ÏóÖÎ¨¥ ÏÇ≠Ï†úÏóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.");
    }
  };

  // ‚úÖ ÌîÑÎ°úÏ†ùÌä∏ Î∞è Task Î™©Î°ù Î∂àÎü¨Ïò§Í∏∞
  useEffect(() => {
    if (projectId) {
      fetchProject(); // ‚úÖ ÌîÑÎ°úÏ†ùÌä∏ Ï†ïÎ≥¥ Í∞ÄÏ†∏Ïò§Í∏∞
      fetchTasks(); // ‚úÖ Task Î™©Î°ù Í∞ÄÏ†∏Ïò§Í∏∞
    }
  }, [projectId]);

return (
  <div className="task-page"> {/* ‚úÖ ÏàòÏ†ï: Ï†ÑÏ≤¥ ÌéòÏù¥ÏßÄ Ïä§ÌÉÄÏùº Ï†ÅÏö© */}
    {/* ‚úÖ ÌîÑÎ°úÏ†ùÌä∏ Ïù¥Î¶Ñ + Íµ¨Î∂ÑÏÑ† */}
    <h2 className="project-title">{project ? project.name : "Î°úÎî© Ï§ë..."}</h2>
    <hr className="title-divider" />

    <div className="task-container"> {/* ‚úÖ ÏàòÏ†ï: ÏóÖÎ¨¥ Î™©Î°ùÍ≥º Í∞ÑÌä∏ Ï∞®Ìä∏Î•º ÎÇòÎûÄÌûà Î∞∞Ïπò */}
      {/* ‚úÖ ÏôºÏ™Ω: ÏóÖÎ¨¥ Î™©Î°ù */}
      <div className="task-list">
        <button onClick={() => { setEditTask(null); setIsModalOpen(true); }}>
          + ÏóÖÎ¨¥ Ï∂îÍ∞Ä
        </button>
        <table>
          <thead>
            <tr>
              <th>ÏóÖÎ¨¥Î™Ö</th>
              <th>ÏÉÅÌÉú</th>
              <th>ÏãúÏûëÏùº</th>
              <th>ÎßàÍ∞êÏùº</th>
              <th>Í¥ÄÎ¶¨</th>
            </tr>
          </thead>
          <tbody>
            {tasks.map((task) => (
              <tr key={task.id}>
                <td>{task.name}</td>
                <td>
                  <span className={`status-badge ${task.status}`}>
                    {task.status}
                  </span>
                </td>
                <td>{task.startDate}</td>
                <td>{task.dueDate}</td>
                <td>
                  <button onClick={() => { setEditTask(task); setIsModalOpen(true); }}>
                    ÏàòÏ†ï
                  </button>
                  <button onClick={() => setSelectedTask(task)} className="detail-btn">
                    ÏÉÅÏÑ∏ Î≥¥Í∏∞
                  </button>
                  <button onClick={() => handleDeleteTask(task.id)} className="delete-btn">
                    ÏÇ≠Ï†ú
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* ‚úÖ Ïò§Î•∏Ï™Ω: Í∞ÑÌä∏ Ï∞®Ìä∏ */}
      <div className="gantt-chart-container">
      <GanttChart tasks={tasks} />
      </div>
    </div>

    {/* ‚úÖ Task Ï∂îÍ∞Ä & ÏàòÏ†ï Î™®Îã¨ */}
    {isModalOpen && (
      <TaskModal
        onClose={() => setIsModalOpen(false)}
        projectId={projectId}
        refreshTasks={fetchTasks}
        editTask={editTask}
      />
    )}

    {/* ‚úÖ Task ÏÉÅÏÑ∏ Î≥¥Í∏∞ Î™®Îã¨ */}
    {selectedTask && (
      <TaskDetailModal task={selectedTask} onClose={() => setSelectedTask(null)} />
    )}
  </div>
);
};

export default TaskPage;
