import React, { useEffect, useState } from "react";
import axios from "axios";
import { getAccessToken } from "../utils/authUtils";
import { Client } from "@stomp/stompjs";
import "../styles/InviteList.css"; // ‚úÖ Ïä§ÌÉÄÏùº Ï†ÅÏö©

const InviteList = ({ refreshProjects }) => {
  const [invites, setInvites] = useState([]);
  const [notifications, setNotifications] = useState([]); // ‚úÖ ÌåùÏóÖ ÏïåÎ¶º ÏÉÅÌÉú

  useEffect(() => {
    const fetchInvites = async () => {
      const token = getAccessToken();
      try {
        const response = await axios.get("http://localhost:8082/api/team/invites", {
          headers: { Authorization: `Bearer ${token}` },
        });
        console.log("üìå Î∞õÏùÄ Ï¥àÎåÄ Î™©Î°ù:", response.data);
        setInvites(response.data);
      } catch (error) {
        console.error("‚ùå Ï¥àÎåÄ Î™©Î°ù Î∂àÎü¨Ïò§Í∏∞ Ïã§Ìå®:", error);
      }
    };

    fetchInvites();

    // ‚úÖ WebSocket Ïó∞Í≤∞ (ÌÜ†ÌÅ∞ Ìè¨Ìï®)
    const stompClient = new Client({
      brokerURL: "ws://localhost:8082/ws", // ‚úÖ WebSocket ÏßÅÏ†ë ÏÇ¨Ïö©
      connectHeaders: {
        Authorization: `Bearer ${getAccessToken()}`, // ‚úÖ WebSocket ÏöîÏ≤≠Ïóê Ïù∏Ï¶ù ÌÜ†ÌÅ∞ Ï∂îÍ∞Ä
      },
      debug: (str) => console.log("üìå STOMP Debug:", str),
      reconnectDelay: 5000,
      heartbeatIncoming: 4000,
      heartbeatOutgoing: 4000,
      onConnect: () => {
        console.log("‚úÖ WebSocket Ïó∞Í≤∞Îê®");
        stompClient.subscribe("/topic/invites", (message) => {
          const newInvite = JSON.parse(message.body);
          console.log("üì© ÏÉàÎ°úÏö¥ Ï¥àÎåÄ ÏïåÎ¶º:", newInvite);
          setNotifications((prev) => [...prev, newInvite]); // ‚úÖ ÌåùÏóÖ ÏïåÎ¶º Ï∂îÍ∞Ä
          fetchInvites(); // ‚úÖ Ï¥àÎåÄ Î™©Î°ù ÏÉàÎ°úÍ≥†Ïπ®
        });
      },
      onStompError: (frame) => {
        console.error("üö® STOMP ERROR:", frame);
      },
    });

    stompClient.activate();

    return () => {
      stompClient.deactivate();
    };
  }, []);

  // ‚úÖ Ï¥àÎåÄ ÏàòÎùΩ Ìï®Ïàò
  const acceptInvite = async (inviteId) => {
    const token = getAccessToken();
    try {
      await axios.post(`http://localhost:8082/api/team/invite/${inviteId}/accept`, null, {
        headers: { Authorization: `Bearer ${token}` },
      });

      alert("‚úÖ Ï¥àÎåÄÎ•º ÏàòÎùΩÌñàÏäµÎãàÎã§!");
      setInvites((prev) => prev.filter((invite) => invite.id !== inviteId));
      refreshProjects(); // ‚úÖ Ï¥àÎåÄ ÏàòÎùΩ ÌõÑ ÌîÑÎ°úÏ†ùÌä∏ Î™©Î°ù Í∞±Ïã†

      // ‚úÖ ÏïåÎ¶º Ï†úÍ±∞ (3Ï¥à ÌõÑ ÏÇ¨ÎùºÏßê)
      setTimeout(() => {
        setNotifications((prev) => prev.filter((notif) => notif.id !== inviteId));
      }, 3000);

    } catch (error) {
      console.error("‚ùå Ï¥àÎåÄ ÏàòÎùΩ Ïã§Ìå®:", error);
    }
  };

  return (
    <>
      {/* ‚úÖ ÌåùÏóÖ ÏïåÎ¶º Ï∞Ω */}
      <div className="invite-popup-container">
        {notifications.map((invite) => (
          <div key={invite.id} className="invite-popup">
            <p>
              <strong>{invite.sender.username}</strong> ÎãòÏù¥ <strong>{invite.project.name}</strong> ÌîÑÎ°úÏ†ùÌä∏Ïóê Ï¥àÎåÄÌñàÏäµÎãàÎã§.
            </p>
            <button onClick={() => acceptInvite(invite.id)}>‚úÖ ÏàòÎùΩ</button>
            <button className="reject-btn">‚ùå Í±∞Ï†à</button>
          </div>
        ))}
      </div>
    </>
  );
};

export default InviteList;
