import React, { useState, useEffect } from "react";
import axios from "axios";
import { getAccessToken } from "../utils/authUtils";
import { getStompClient } from "../api/websocket"; // ‚úÖ WebSocket ÌÅ¥ÎùºÏù¥Ïñ∏Ìä∏ Í∞ÄÏ†∏Ïò§Í∏∞

const Chatbox = ({ projectId }) => { // ‚úÖ ÌîÑÎ°úÏ†ùÌä∏ IDÎ•º propsÎ°ú Î∞õÏùå
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState("");

  // ‚úÖ Ï±ÑÌåÖ Î©îÏãúÏßÄ Î∂àÎü¨Ïò§Í∏∞ Ìï®Ïàò
  const fetchChatMessages = async () => {
    if (!projectId) {
      console.warn("‚ùå ÌîÑÎ°úÏ†ùÌä∏ IDÍ∞Ä Ï†ïÏùòÎêòÏßÄ ÏïäÏùå, Ï±ÑÌåÖ Î∂àÎü¨Ïò§ÏßÄ ÏïäÏùå.");
      return;
    }

    const token = getAccessToken();
    try {
      const response = await axios.get(`http://localhost:8082/api/chat/${projectId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setMessages(response.data);
      console.log("‚úÖ Ï±ÑÌåÖ Î©îÏãúÏßÄ Î°úÎìú ÏÑ±Í≥µ:", response.data);
    } catch (error) {
      console.error("‚ùå Ï±ÑÌåÖ Í∏∞Î°ù Î∂àÎü¨Ïò§Í∏∞ Ïã§Ìå®:", error);
    }
  };

  useEffect(() => {
    console.log(`üîÑ ChatboxÏóêÏÑú Í∞êÏßÄÎêú ÌîÑÎ°úÏ†ùÌä∏ ID: ${projectId}`);
    if (projectId) { // ‚úÖ ÌîÑÎ°úÏ†ùÌä∏ IDÍ∞Ä ÏûàÏùÑ ÎïåÎßå Ïã§Ìñâ
      fetchChatMessages();
    }
  }, [projectId]);


  // ‚úÖ WebSocket Î©îÏãúÏßÄ Ï†ÑÏÜ°
  const sendMessage = async () => {
    if (!newMessage.trim()) return;
    if (!projectId) {
      console.warn("‚ùå ÌîÑÎ°úÏ†ùÌä∏ IDÍ∞Ä ÏóÜÏùå, Î©îÏãúÏßÄÎ•º Ï†ÑÏÜ°Ìï† Ïàò ÏóÜÏùå.");
      return;
    }

    const token = getAccessToken();
    const stompClient = getStompClient();

    const messageData = {
      project: { id: projectId }, // ‚úÖ WebSocketÏóêÏÑúÎäî bodyÎ°ú projectId Ìè¨Ìï®
      sender: { email: localStorage.getItem("userEmail") }, // ‚úÖ ÌòÑÏû¨ ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥ Ï∂îÍ∞Ä
      content: newMessage,
    };

    try {
      stompClient.publish({
        destination: `/app/chat/${projectId}`, // ‚úÖ WebSocket ÏóîÎìúÌè¨Ïù∏Ìä∏ ÏàòÏ†ï
        body: JSON.stringify(messageData),
      });

      console.log("üì§ Î©îÏãúÏßÄ Ï†ÑÏÜ°:", newMessage);
      setNewMessage("");
    } catch (error) {
      console.error("‚ùå Î©îÏãúÏßÄ Ï†ÑÏÜ° Ïã§Ìå®:", error);
    }
  };

  return (
    <div className="chatbox-container">
      <h3>ÌåÄ Ï±ÑÌåÖ</h3>
      {projectId ? (
        <>
          <div className="messages">
            {messages.length > 0 ? (
              messages.map((msg) => (
                <p key={msg.id}><strong>{msg.sender.username}</strong>: {msg.content}</p>
              ))
            ) : (
              <p>üì≠ ÏïÑÏßÅ Î©îÏãúÏßÄÍ∞Ä ÏóÜÏäµÎãàÎã§.</p>
            )}
          </div>
          <input
            type="text"
            value={newMessage}
            onChange={(e) => setNewMessage(e.target.value)}
            placeholder="Î©îÏãúÏßÄÎ•º ÏûÖÎ†•ÌïòÏÑ∏Ïöî..."
          />
          <button onClick={sendMessage}>Ï†ÑÏÜ°</button>
        </>
      ) : (
        <p>üö® ÌîÑÎ°úÏ†ùÌä∏Î•º ÏÑ†ÌÉùÌï¥Ï£ºÏÑ∏Ïöî.</p> // ‚úÖ ÌîÑÎ°úÏ†ùÌä∏Í∞Ä ÏóÜÏùÑ Îïå ÏïàÎÇ¥ Î©îÏãúÏßÄ
      )}
    </div>
  );
};

export default Chatbox;
