import axios from "axios";

const API_BASE_URL = "http://localhost:8082/api/auth"; // ‚úÖ Î∞±ÏóîÎìú Í≤ΩÎ°ú ÌôïÏù∏

export const signup = async (name, email, password) => {
    try {
        const response = await axios.post("http://localhost:8082/api/auth/register", {
            username: name,
            email,
            password
        }, { withCredentials: true });

        // ‚úÖ Î∞±ÏóîÎìúÏóêÏÑú ÏÑ±Í≥µ ÏùëÎãµ Ïãú message ÌïÑÎìúÍ∞Ä Ï°¥Ïû¨ÌïòÎ©¥ ÏÑ±Í≥µ Ï≤òÎ¶¨
        if (response.status === 200) {
            return { success: true, message: response.data };
        } else {
            return { success: false, message: "ÌöåÏõêÍ∞ÄÏûÖ Ïã§Ìå®" };
        }
    } catch (error) {
        return { 
            success: false, 
            message: error.response?.data || "ÌöåÏõêÍ∞ÄÏûÖ Ïã§Ìå®" 
        };
    }
};


