import SockJS from "sockjs-client";
import Stomp from "stompjs";
import axios from "axios";

const connectToChat = async () => {
  try {
    // 1Ô∏è‚É£ Secondary Token ÏöîÏ≤≠
    const response = await axios.post("http://localhost:8082/api/user/secondary-token", {}, { withCredentials: true });
    const secondaryToken = response.data.secondaryToken;

    console.log("‚úÖ Secondary Token:", secondaryToken);

    // 2Ô∏è‚É£ WebSocket Ïó∞Í≤∞ (Secondary Token Ìè¨Ìï®)
    const socket = new SockJS(`http://localhost:8082/ws?secondaryToken=${secondaryToken}`);
    const stompClient = Stomp.over(socket);

    // 3Ô∏è‚É£ WebSocket Ïó∞Í≤∞ ÏãúÎèÑ
    stompClient.connect({}, (frame) => {
      console.log("üü¢ WebSocket Ïó∞Í≤∞ ÏÑ±Í≥µ:", frame);

      // 4Ô∏è‚É£ ÌäπÏ†ï Ï±ÑÌåÖÎ∞© Íµ¨ÎèÖ (teamId = 1 ÏòàÏãú)
      stompClient.subscribe("/topic/chat/1", (message) => {
        console.log("üì© ÏàòÏã†Îêú Î©îÏãúÏßÄ:", JSON.parse(message.body));
      });

      // 5Ô∏è‚É£ Î©îÏãúÏßÄ Î≥¥ÎÇ¥Í∏∞ (ÏòàÏ†ú)
      stompClient.send("/app/chat/send", {}, JSON.stringify({
        content: "Hello Team!",
        teamId: 1
      }));

    }, (error) => {
      console.error("‚õî WebSocket Ïó∞Í≤∞ Ïã§Ìå®:", error);
    });

  } catch (error) {
    console.error("‚ùå Secondary Token ÏöîÏ≤≠ Ïã§Ìå®:", error);
  }
};

export default connectToChat;
