import React, { useState, useEffect } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import axios from "axios";
import { getAccessToken } from "../utils/authUtils";
import TaskModal from "../components/TaskModal";

const TaskPage = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const queryParams = new URLSearchParams(location.search);
  const projectId = queryParams.get("projectId");

  const [tasks, setTasks] = useState([]);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [editTask, setEditTask] = useState(null); //ÏàòÏ†ïÌï† Task Ï†ÄÏû•

  // ‚úÖ Task Î™©Î°ù Î∂àÎü¨Ïò§Í∏∞
  const fetchTasks = async () => {
    const token = getAccessToken();
    if (!token) {
      alert("Î°úÍ∑∏Ïù∏Ïù¥ ÌïÑÏöîÌï©ÎãàÎã§.");
      navigate("/login");
      return;
    }

    try {
      const response = await axios.get(`http://localhost:8082/api/user/tasks?projectId=${projectId}`, {
        headers: {
          Authorization: `Bearer ${token}`,
          "Content-Type": "application/json",
        },
        withCredentials: true,
      });

      setTasks(response.data);
    } catch (error) {
      console.error("‚ùå Task Î™©Î°ù Î∂àÎü¨Ïò§Í∏∞ Ïã§Ìå®:", error);
    }
  };

  // ‚úÖ Task Ï∂îÍ∞Ä ÌõÑ Î™©Î°ù Í∞±Ïã†
  const refreshTasks = () => {
    fetchTasks();
  };

    // ‚úÖ Task ÏÇ≠Ï†ú Ìï®Ïàò 
    const handleDeleteTask = async (taskId) => {
      const token = getAccessToken();
      if (!token) {
        alert("Î°úÍ∑∏Ïù∏Ïù¥ ÌïÑÏöîÌï©ÎãàÎã§.");
        return;
      }
  
      if (!window.confirm("Ï†ïÎßêÎ°ú Ïù¥ ÏóÖÎ¨¥Î•º ÏÇ≠Ï†úÌïòÏãúÍ≤†ÏäµÎãàÍπå?")) return;
  
      try {
        await axios.delete(`http://localhost:8082/api/user/tasks/${taskId}`, {
          headers: {
            Authorization: `Bearer ${token}`,
            "Content-Type": "application/json",
          },
          withCredentials: true,
        });
  
        alert("ÏóÖÎ¨¥Í∞Ä ÏÑ±Í≥µÏ†ÅÏúºÎ°ú ÏÇ≠Ï†úÎêòÏóàÏäµÎãàÎã§.");
        refreshTasks(); // ‚úÖ ÏÇ≠Ï†ú ÌõÑ Î™©Î°ù ÏÉàÎ°úÍ≥†Ïπ®
      } catch (error) {
        console.error("‚ùå Task ÏÇ≠Ï†ú Ïã§Ìå®:", error);
        alert("ÏóÖÎ¨¥ ÏÇ≠Ï†úÏóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.");
      }
    };

  useEffect(() => {
    if (projectId) {
      fetchTasks();
    }
  }, [projectId]);

  return (
    <div>
      <h1>Task Í¥ÄÎ¶¨</h1>
      <button onClick={() => { setEditTask(null); setIsModalOpen(true); }}>+ ÏóÖÎ¨¥ Ï∂îÍ∞Ä</button>

      {/* ‚úÖ Task Î™©Î°ù ÌëúÏãú */}
      <ul>
        {tasks.map((task) => (
          <li key={task.id}>
            <strong>{task.name}</strong> - {task.status} 
            <br /> üóì **ÏãúÏûëÏùº**: {task.startDate} | ‚è≥ **ÎßàÍ∞êÏùº**: {task.dueDate}  
            <button onClick={() => { setEditTask(task); setIsModalOpen(true); }}>ÏàòÏ†ï</button> {/* ‚úÖ ÏàòÏ†ï Î≤ÑÌäº Ï∂îÍ∞Ä */}
            <button onClick={() => handleDeleteTask(task.id)} style={{ marginLeft: "10px", color: "red" }}>ÏÇ≠Ï†ú</button> {/* ‚úÖ ÏÇ≠Ï†ú Î≤ÑÌäº Ï∂îÍ∞Ä */}
            </li>
        ))}
      </ul>

      {/* ‚úÖ Task Ï∂îÍ∞Ä & ÏàòÏ†ï Î™®Îã¨ */}
      {isModalOpen && (
        <TaskModal
          onClose={() => setIsModalOpen(false)}
          projectId={projectId}
          refreshTasks={refreshTasks}
          editTask={editTask} // ‚úÖ ÏàòÏ†ïÌï† Task Ï†ÑÎã¨
        />
      )}
    </div>
  );
};

export default TaskPage;
