import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import "../styles/Navbar.css";

const Navbar = () => {
  const [invitations, setInvitations] = useState([]); 
  const [showPopup, setShowPopup] = useState(false);
  const [user, setUser] = useState(null);

  const fetchUserInfo = async () => {
    try {
        const token = localStorage.getItem("token");
        if (!token) {
            console.error("üö® JWT ÌÜ†ÌÅ∞Ïù¥ ÏóÜÏäµÎãàÎã§! Î°úÍ∑∏Ïù∏Ïù¥ ÌïÑÏöîÌï©ÎãàÎã§.");
            return;
        }

        const response = await axios.get("http://localhost:8082/api/user", {
            headers: {
                Authorization: `Bearer ${token}`, // ‚úÖ JWT ÌÜ†ÌÅ∞ Ï∂îÍ∞Ä
                "Content-Type": "application/json",
            },
            withCredentials: true, // ‚úÖ JWT Ìè¨Ìï® ÏöîÏ≤≠
        });

        console.log("‚úÖ ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥ Î∂àÎü¨Ïò§Í∏∞ ÏÑ±Í≥µ:", response.data);
        localStorage.setItem("user", JSON.stringify(response.data));
        setUser(response.data);  // ‚úÖ ÏÇ¨Ïö©Ïûê ÏÉÅÌÉú ÏóÖÎç∞Ïù¥Ìä∏
    } catch (error) {
        console.error("‚ùå ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥ Î∂àÎü¨Ïò§Í∏∞ Ïã§Ìå®:", error);
    }
};


// useEffect ÎÇ¥Î∂ÄÏóêÏÑú Ïã§Ìñâ
useEffect(() => {
    fetchUserInfo();
}, []);


  // ‚úÖ Ï¥àÎåÄ Î™©Î°ù Î∂àÎü¨Ïò§Í∏∞
  const fetchInvitations = async () => {
    try {
      const token = localStorage.getItem("token");
      if (!token) return;

      const response = await axios.get("http://localhost:8082/api/team/invitations", {
        headers: { Authorization: `Bearer ${token}` },
      });

      setInvitations(response.data);
      if (response.data.length > 0) setShowPopup(true);
    } catch (error) {
      console.error("Ï¥àÎåÄ Î™©Î°ù Î∂àÎü¨Ïò§Í∏∞ Ïã§Ìå®:", error);
    }
  };

  // ‚úÖ Î°úÍ∑∏ÏïÑÏõÉ Ìï®Ïàò
  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("refreshToken");
    localStorage.removeItem("user");
    setUser(null); // ‚úÖ ÏÉÅÌÉú ÏóÖÎç∞Ïù¥Ìä∏ÌïòÏó¨ UI Î≥ÄÍ≤Ω
    window.location.href = "/login"; // Î°úÍ∑∏Ïù∏ ÌéòÏù¥ÏßÄÎ°ú Ïù¥Îèô
  };

  return (
    <nav className="navbar">
      <div className="navbar-logo">TeamVerse</div>

      {/* ‚úÖ Ï¥àÎåÄ ÌåùÏóÖ */}
      {showPopup && (
        <div className="invitation-popup">
          <h2>Ï¥àÎåÄ ÏïåÎ¶º</h2>
          <ul>
            {invitations.map((invite) => (
              <li key={invite.id}>
                <p>{invite.email}ÎãòÏù¥ ÌåÄ Ï¥àÎåÄÎ•º Î≥¥ÎÉàÏäµÎãàÎã§.</p>

              </li>
            ))}
          </ul>
          <button className="close-btn" onClick={() => setShowPopup(false)}>Îã´Í∏∞</button>
        </div>
      )}

      <div className="navbar-links">
        <Link to="/">Dashboard</Link>
        <Link to="/statistics">Statistics</Link>
        <Link to="/team-status">Team</Link>
        <Link to="/settings">Settings</Link>
        {user ? (
          <div className="user-info">
            <span>Welcome, {user.username}!</span>
            <button onClick={handleLogout}>Logout</button>
          </div>
        ) : (
          <Link to="/login">Login</Link>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
