import React, { useState, useEffect } from "react";
import axios from "axios";
import { getAccessToken } from "../utils/authUtils";
import { getStompClient } from "../api/websocket";
import "../styles/Chatbox.css"; 

const Chatbox = ({ projectId }) => {
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState("");

  // ‚úÖ Ï±ÑÌåÖ Î©îÏãúÏßÄ Î∂àÎü¨Ïò§Í∏∞ (Ï¥àÍ∏∞ Î°úÎî© Ïãú)
  const fetchChatMessages = async () => {
    if (!projectId) return;
    const token = getAccessToken();
    try {
      const response = await axios.get(`http://localhost:8082/api/chat/${projectId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setMessages(response.data); // ‚úÖ ÏÉÅÌÉú ÏóÖÎç∞Ïù¥Ìä∏
      console.log("‚úÖ Ï±ÑÌåÖ Î©îÏãúÏßÄ Î°úÎìú ÏÑ±Í≥µ:", response.data);
    } catch (error) {
      console.error("‚ùå Ï±ÑÌåÖ Í∏∞Î°ù Î∂àÎü¨Ïò§Í∏∞ Ïã§Ìå®:", error);
    }
  };

  useEffect(() => {
    if (!projectId) return;
    console.log(`üîÑ ChatboxÏóêÏÑú Í∞êÏßÄÎêú ÌîÑÎ°úÏ†ùÌä∏ ID: ${projectId}`);
    fetchChatMessages();

    const stompClient = getStompClient();
    if (!stompClient.connected) stompClient.activate();

    // ‚úÖ WebSocket Íµ¨ÎèÖ (ÏÉàÎ°úÏö¥ Î©îÏãúÏßÄ ÏàòÏã†)
    const subscription = stompClient.subscribe(`/topic/chat/${projectId}`, (message) => {
      const receivedMessage = JSON.parse(message.body);
      console.log("üì© ÏÉà Ï±ÑÌåÖ Î©îÏãúÏßÄ ÏàòÏã†:", receivedMessage);
      setMessages((prevMessages) => [...prevMessages, receivedMessage]); 
    });

    return () => {
      console.log("üõë WebSocket Íµ¨ÎèÖ Ìï¥Ï†ú:", `/topic/chat/${projectId}`);
      subscription.unsubscribe();
    };
  }, [projectId]);

  // ‚úÖ Î©îÏãúÏßÄ Ï†ÑÏÜ°
  const sendMessage = async () => {
    if (!newMessage.trim() || !projectId) return;
    const userEmail = localStorage.getItem("email") || sessionStorage.getItem("email");
    if (!userEmail) {
      console.error("‚ùå Î°úÍ∑∏Ïù∏Ìïú ÏÇ¨Ïö©ÏûêÏùò Ïù¥Î©îÏùºÏùÑ Ï∞æÏùÑ Ïàò ÏóÜÏäµÎãàÎã§!");
      return;
    }

    try {
      const messageData = {
        project: { id: projectId },
        sender: { email: userEmail },
        content: newMessage.trim(),
      };

      const stompClient = getStompClient();
      if (!stompClient.connected) {
        console.error("üö® WebSocketÏù¥ Ïó∞Í≤∞ÎêòÏßÄ ÏïäÏùå!");
        return;
      }

      stompClient.publish({
        destination: `/app/chat`,
        body: JSON.stringify(messageData),
      });

      console.log("üì§ Î©îÏãúÏßÄ Ï†ÑÏÜ°:", messageData);
      setNewMessage("");
    } catch (error) {
      console.error("‚ùå Î©îÏãúÏßÄ Ï†ÑÏÜ° Ïã§Ìå®:", error);
    }
  };

  return (
    <div className="chatbox-container">
      <h3>ÌåÄ Ï±ÑÌåÖ</h3>
      <div className="messages">
        {messages.length > 0 ? (
          messages.map((msg, index) => (
            <p key={index}><strong>{msg.sender.email}</strong>: {msg.content}</p>
          ))
        ) : (
          <p>üì≠ ÏïÑÏßÅ Î©îÏãúÏßÄÍ∞Ä ÏóÜÏäµÎãàÎã§.</p>
        )}
      </div>
      <input
        type="text"
        value={newMessage}
        onChange={(e) => setNewMessage(e.target.value)}
        placeholder="Î©îÏãúÏßÄÎ•º ÏûÖÎ†•ÌïòÏÑ∏Ïöî..."
      />
      <button onClick={sendMessage}>Ï†ÑÏÜ°</button>
    </div>
  );
};

export default Chatbox;
