import React, { useEffect, useContext } from 'react';
import { Router, Routes, Route } from 'react-router-dom';
import MainPage from './pages/MainPage';
import LoginPage from './pages/LoginPage';
import Dashboard from './pages/Dashboard'; // üÜï Dashboard ÌéòÏù¥ÏßÄ Ï∂îÍ∞Ä
import Navbar from './components/Navbar';
import StatisticsPage from './pages/StatisticsPage';
import TeamStatusPage from './pages/TeamStatusPage';
import ProtectedRoute from './components/ProtectedRoute'; // üÜï Î°úÍ∑∏Ïù∏Ìïú ÏÇ¨Ïö©ÏûêÎßå Ï†ëÍ∑º Í∞ÄÎä•ÌïòÎèÑÎ°ù Î≥¥Ìò∏
import { refreshToken } from './api/authApi'; // üÜï Refresh TokenÏùÑ Ïù¥Ïö©Ìïú Access Token Í∞±Ïã† Ìï®Ïàò Ï∂îÍ∞Ä
import { AuthContext } from './context/AuthContext'; // üÜï Î°úÍ∑∏Ïù∏ ÏÉÅÌÉú Í¥ÄÎ¶¨Î•º ÏúÑÌïú AuthContext Ï∂îÍ∞Ä

function App() {
  const { user, logout } = useContext(AuthContext); // üÜï ÌòÑÏû¨ Î°úÍ∑∏Ïù∏Îêú ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥ÏôÄ Î°úÍ∑∏ÏïÑÏõÉ Ìï®Ïàò Í∞ÄÏ†∏Ïò§Í∏∞

  useEffect(() => {
    // üÜï 10Î∂ÑÎßàÎã§ Access Token ÏûêÎèô Í∞±Ïã†
    const interval = setInterval(async () => {
      try {
        const newAccessToken = await refreshToken(); // Refresh TokenÏùÑ Ïù¥Ïö©ÌïòÏó¨ Access Token Í∞±Ïã† ÏãúÎèÑ
        if (newAccessToken) {
          console.log("üü¢ ÏÉà ÌÜ†ÌÅ∞ Î∞úÍ∏â:", newAccessToken);
          localStorage.setItem("accessToken", newAccessToken); // üÜï ÏÉàÎ°úÏö¥ Access TokenÏùÑ localStorageÏóê Ï†ÄÏû•
        } else {
          console.warn("üî¥ ÌÜ†ÌÅ∞ Í∞±Ïã† Ïã§Ìå®: Î°úÍ∑∏ÏïÑÏõÉ Ï≤òÎ¶¨");
          logout(); // üÜï ÌÜ†ÌÅ∞ Í∞±Ïã† Ïã§Ìå® Ïãú ÏûêÎèô Î°úÍ∑∏ÏïÑÏõÉ
        }
      } catch (error) {
        console.error("üî¥ ÌÜ†ÌÅ∞ Í∞±Ïã† Ï§ë Ïò§Î•ò Î∞úÏÉù:", error);
        logout(); // üÜï Ïò§Î•ò Î∞úÏÉù Ïãú ÏûêÎèô Î°úÍ∑∏ÏïÑÏõÉ
      }
    }, 10 * 60 * 1000); // üÜï 10Î∂Ñ(600,000ms)ÎßàÎã§ Ïã§Ìñâ

    return () => clearInterval(interval); // üÜï Ïª¥Ìè¨ÎÑåÌä∏Í∞Ä Ïñ∏ÎßàÏö¥Ìä∏Îê† Îïå interval Ï†úÍ±∞
  }, [logout]); // üÜï Î°úÍ∑∏ÏïÑÏõÉ Ìï®ÏàòÍ∞Ä Î≥ÄÍ≤ΩÎê† Í≤ΩÏö∞ useEffect Ïû¨Ïã§Ìñâ

  return (
    <Router>
      <div className="app">
        <Navbar />
        <Routes>
          <Route path="/" element={<MainPage />} />
          <Route path="/login" element={<LoginPage />} />
          <Route path="/dashboard" element={
            <ProtectedRoute> {/* üÜï Î°úÍ∑∏Ïù∏Ìïú ÏÇ¨Ïö©ÏûêÎßå Dashboard Ï†ëÍ∑º Í∞ÄÎä•ÌïòÎèÑÎ°ù Î≥¥Ìò∏ */}
              <Dashboard />
            </ProtectedRoute>
          } />
          <Route path="/statistics" element={<StatisticsPage />} /> {/* üÜï StatisticsPageÎ°ú Ïù¥ÎèôÌïòÎäî Í≤ΩÎ°ú ÏÑ§Ï†ï */}
          <Route path="/team-status" element={<TeamStatusPage />} /> {/* üÜï TeamStatusPageÎ°ú Ïù¥ÎèôÌïòÎäî Í≤ΩÎ°ú ÏÑ§Ï†ï */}
        </Routes>
      </div>
    </Router>
  );
}

export default App;
