import React, { useState, useEffect } from "react";
import Sidebar from "../components/Sidebar";
import GanttChart from "../components/GanttChart";
import Chatbox from "../components/Chatbox";
import Dashboard from "../components/Dashboard";
import TaskBoard from "../components/TaskBoard";
import "../styles/MainPage.css";
import axios from "axios";
import { useNavigate, useParams } from "react-router-dom";

const MainPage = () => {
    const [projects, setProjects] = useState([]); // ‚úÖ ÌîÑÎ°úÏ†ùÌä∏ Î™©Î°ù Ï†ÄÏû•
    const [selectedProject, setSelectedProject] = useState(null); // ‚úÖ ÏÑ†ÌÉùÌïú ÌîÑÎ°úÏ†ùÌä∏ Ï†ÄÏû•
    const [showModal, setShowModal] = useState(false);
    const [projectName, setProjectName] = useState("");
    const [user, setUser] = useState(null);
    const { userId } = useParams();
    const navigate = useNavigate();

    // ‚úÖ ÌîÑÎ°úÏ†ùÌä∏ Î™©Î°ù Î∂àÎü¨Ïò§Í∏∞
    useEffect(() => {
        const fetchProjects = async () => {
            const token = localStorage.getItem("accessToken");
            if (!token) return;

            try {
                const response = await axios.get("http://localhost:8082/api/user/projects", {
                    headers: {
                        Authorization: `Bearer ${token}`,
                        "Content-Type": "application/json",
                    },
                });

                console.log("‚úÖ ÌîÑÎ°úÏ†ùÌä∏ Î™©Î°ù:", response.data);
                setProjects(response.data);

                // ‚úÖ Ï†ÄÏû•Îêú ÌîÑÎ°úÏ†ùÌä∏ IDÍ∞Ä ÏûàÎã§Î©¥ ÏûêÎèô ÏÑ†ÌÉù
                const savedProjectId = localStorage.getItem("selectedProjectId");
                if (savedProjectId) {
                    const foundProject = response.data.find(proj => proj.id === parseInt(savedProjectId));
                    if (foundProject) {
                        setSelectedProject(foundProject);
                    }
                }
            } catch (error) {
                console.error("‚ùå ÌîÑÎ°úÏ†ùÌä∏ Î™©Î°ù Î∂àÎü¨Ïò§Í∏∞ Ïã§Ìå®:", error);
                alert("ÌîÑÎ°úÏ†ùÌä∏ Îç∞Ïù¥ÌÑ∞Î•º Î∂àÎü¨Ïò¨ Ïàò ÏóÜÏäµÎãàÎã§.");
            }
        };

        fetchProjects();
    }, []);

    // ‚úÖ ÏÉàÎ°úÏö¥ ÌîÑÎ°úÏ†ùÌä∏ ÏÉùÏÑ±
    const handleCreateProject = async () => {
        const token = localStorage.getItem("accessToken");
        if (!token) {
            alert("Î°úÍ∑∏Ïù∏Ïù¥ ÌïÑÏöîÌï©ÎãàÎã§.");
            return;
        }

        try {
            const response = await axios.post(
                "http://localhost:8082/api/user/projects",
                { name: projectName, startDate: new Date().toISOString().split("T")[0] },
                {
                    headers: {
                        Authorization: `Bearer ${token}`,
                        "Content-Type": "application/json",
                    },
                }
            );

            console.log("‚úÖ ÌîÑÎ°úÏ†ùÌä∏ ÏÉùÏÑ± ÏÑ±Í≥µ:", response.data);
            setProjects([...projects, response.data]);
            setSelectedProject(response.data);
            localStorage.setItem("selectedProjectId", response.data.id);
            setShowModal(false);
        } catch (error) {
            console.error("‚ùå ÌîÑÎ°úÏ†ùÌä∏ ÏÉùÏÑ± Ïã§Ìå®:", error);
            alert("ÌîÑÎ°úÏ†ùÌä∏ ÏÉùÏÑ±Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.");
        }
    };

    return (
        <div className="main-page">
            <div className="content">
                {/* ‚úÖ ÌîÑÎ°úÏ†ùÌä∏ Î™©Î°ù ÌëúÏãú */}
                <div className="project-list">
                    <h2>üìÇ ÌîÑÎ°úÏ†ùÌä∏ Î™©Î°ù</h2>
                    {projects.length === 0 ? (
                        <p>ÌòÑÏû¨ ÌîÑÎ°úÏ†ùÌä∏Í∞Ä ÏóÜÏäµÎãàÎã§.</p>
                    ) : (
                        <ul>
                            {projects.map((project) => (
                                <li key={project.id}>
                                    <button
                                        className={`project-btn ${selectedProject?.id === project.id ? "active" : ""}`}
                                        onClick={() => {
                                            setSelectedProject(project);
                                            localStorage.setItem("selectedProjectId", project.id);
                                        }}
                                    >
                                        {project.name || project.title || project.projectName || "üö® Ïù¥Î¶Ñ ÏóÜÏùå"}
                                    </button>
                                </li>
                            ))}
                        </ul>
                    )}
                    <button className="create-project-btn" onClick={() => setShowModal(true)}>
                        ÏÉàÎ°úÏö¥ ÌîÑÎ°úÏ†ùÌä∏ ÏÉùÏÑ±
                    </button>
                </div>

                {/* ‚úÖ ÏÑ†ÌÉùÌïú ÌîÑÎ°úÏ†ùÌä∏Ïùò Í∞ÑÌä∏ Ï∞®Ìä∏ ÌëúÏãú */}
                {selectedProject ? (
                    <div className="project-details">
                        <h2>üìä {selectedProject.name} - Gantt Chart</h2>
                        <GanttChart project={selectedProject} />
                    </div>
                ) : (
                    <p className="no-project-selected">üìå ÌîÑÎ°úÏ†ùÌä∏Î•º ÏÑ†ÌÉùÌï¥Ï£ºÏÑ∏Ïöî.</p>
                )}

                <TaskBoard />
                <Dashboard tasks={[]} />
            </div>
            <div className="chatbox-container">
                <Sidebar />
                <Chatbox />
            </div>

            {/* üîπ ÌîÑÎ°úÏ†ùÌä∏ ÏÉùÏÑ± Î™®Îã¨ */}
            {showModal && (
                <div className="modal">
                    <div className="modal-content">
                        <h2>ÏÉà ÌîÑÎ°úÏ†ùÌä∏ ÏÉùÏÑ±</h2>
                        <input
                            type="text"
                            placeholder="ÌîÑÎ°úÏ†ùÌä∏ Ïù¥Î¶Ñ"
                            value={projectName}
                            onChange={(e) => setProjectName(e.target.value)}
                        />
                        <button onClick={handleCreateProject}>ÏÉùÏÑ±</button>
                        <button onClick={() => setShowModal(false)}>Ï∑®ÏÜå</button>
                    </div>
                </div>
            )}
        </div>
    );
};

export default MainPage;
