import React, { useState, useEffect } from "react";
import Sidebar from "../components/Sidebar";
import GanttChart from "../components/GanttChart";
import Chatbox from "../components/Chatbox";
import Dashboard from "../components/Dashboard";
import TaskBoard from "../components/TaskBoard";
import "../styles/MainPage.css";
import axios from "axios";
import { useNavigate, useParams } from "react-router-dom";

const MainPage = () => {
    const [projects, setProjects] = useState([]);
    const [selectedProject, setSelectedProject] = useState(null);
    const [showModal, setShowModal] = useState(false);
    const [projectName, setProjectName] = useState("");
    const [user, setUser] = useState(null);
    const { userId } = useParams(); // ‚úÖ userId Í∞ÄÏ†∏Ïò§Í∏∞
    const navigate = useNavigate(); // ÏàòÏ†ï: useNavigate ÌõÖ ÏÇ¨Ïö©


    // üîπ Î°úÍ∑∏Ïù∏Ìïú Ïú†Ï†Ä Ï†ïÎ≥¥ Í∞ÄÏ†∏Ïò§Í∏∞
    const fetchUserInfo = async () => {
        const token = localStorage.getItem("accessToken");
        if (!token) {
            navigate("/login"); // ÏàòÏ†ï: Î°úÍ∑∏Ïù∏ÌïòÏßÄ ÏïäÏùÄ Í≤ΩÏö∞ Î°úÍ∑∏Ïù∏ ÌéòÏù¥ÏßÄÎ°ú Ïù¥Îèô
            return;
        }

        try {
            const response = await axios.get("http://localhost:8082/api/auth/me", {
                headers: {
                    Authorization: `Bearer ${token}`,
                    "Content-Type": "application/json",
                },
            });

            console.log("‚úÖ Î°úÍ∑∏Ïù∏Ìïú ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥:", response.data);
            setUser(response.data);
            localStorage.setItem("user", JSON.stringify(response.data));


            // ÏàòÏ†ï: Î°úÍ∑∏Ïù∏ ÌõÑ ÏÇ¨Ïö©Ïûê ID Í∏∞Î∞ò URL Ïù¥Îèô
            navigate(`/dashboard/${response.data.id}`);

        } catch (error) {
            console.error("‚ùå ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥ Î∂àÎü¨Ïò§Í∏∞ Ïã§Ìå®:", error);
            navigate("/login"); // ÏàòÏ†ï: ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥ Î°úÎìú Ïã§Ìå® Ïãú Î°úÍ∑∏Ïù∏ ÌéòÏù¥ÏßÄÎ°ú Ïù¥Îèô

        }
    };

    // üîπ Î°úÍ∑∏Ïù∏Ìïú Ïú†Ï†ÄÏùò ÌîÑÎ°úÏ†ùÌä∏ Î∂àÎü¨Ïò§Í∏∞
    const fetchProjects = async () => {
        const token = localStorage.getItem("accessToken");
        if (!token) return;

        try {
            const response = await axios.get("http://localhost:8082/api/user/projects", {
                headers: {
                    Authorization: `Bearer ${token}`,
                    "Content-Type": "application/json",
                },
            });

            console.log("‚úÖ Î°úÍ∑∏Ïù∏Ìïú Ïú†Ï†ÄÏùò ÌîÑÎ°úÏ†ùÌä∏ Î™©Î°ù:", response.data);
            setProjects(response.data);

            const savedProjectId = localStorage.getItem("selectedProjectId");
            if (savedProjectId) {
                const foundProject = response.data.find(proj => proj.id === parseInt(savedProjectId));
                if (foundProject) {
                    setSelectedProject(foundProject);
                }
            }
        } catch (error) {
            console.error("‚ùå ÌîÑÎ°úÏ†ùÌä∏ Î™©Î°ù Î∂àÎü¨Ïò§Í∏∞ Ïã§Ìå®:", error);
        }
    };

    // ‚úÖ Ï≤òÏùå Î†åÎçîÎßÅÎê† Îïå Ïú†Ï†Ä Ï†ïÎ≥¥ Î∞è ÌîÑÎ°úÏ†ùÌä∏ Í∞ÄÏ†∏Ïò§Í∏∞
    // useEffect(() => {
    //     fetchUserInfo();
    //     fetchProjects();
    // }, []);
    useEffect(() => {
        const fetchUserInfo = async () => {
            const token = localStorage.getItem("accessToken");
            if (!token) {
                console.error("üö® ÌÜ†ÌÅ∞Ïù¥ ÏóÜÏäµÎãàÎã§! Î°úÍ∑∏Ïù∏ ÌéòÏù¥ÏßÄÎ°ú Ïù¥ÎèôÌï©ÎãàÎã§.");
                navigate("/login");
                return;
            }

            try {
                const response = await axios.get("http://localhost:8082/api/auth/me", {
                    headers: { Authorization: `Bearer ${token}` },
                });

                console.log("üìå MainPageÏóêÏÑú Í∞ÄÏ†∏Ïò® ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥:", response.data);

                if (response.data.id !== parseInt(userId)) {
                    console.warn("üö® URLÏùò userIdÏôÄ Î°úÍ∑∏Ïù∏Ìïú ÏÇ¨Ïö©Ïûê IDÍ∞Ä Îã§Î¶Ñ!");
                    navigate(`/dashboard/${response.data.id}`);
                }

                setUser(response.data);
            } catch (error) {
                console.error("‚ùå ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥Î•º Î∂àÎü¨Ïò§Îäî Îç∞ Ïã§Ìå®ÌñàÏäµÎãàÎã§:", error);
                navigate("/login"); // üö® 401 Unauthorized ÏùëÎãµÏù¥ Ïò§Î©¥ Î°úÍ∑∏Ïù∏ ÌéòÏù¥ÏßÄÎ°ú Ïù¥Îèô
            }
        };

        fetchUserInfo();
    }, [userId, navigate]);



    const handleCreateProject = async () => {
        const token = localStorage.getItem("accessToken");
        if (!token) {
            alert("Î°úÍ∑∏Ïù∏Ïù¥ ÌïÑÏöîÌï©ÎãàÎã§.");
            return;
        }

        try {
            const response = await axios.post(
                "http://localhost:8082/api/user/projects",
                { name: projectName, startDate: new Date().toISOString().split("T")[0] },
                {
                    headers: {
                        Authorization: `Bearer ${token}`,
                        "Content-Type": "application/json",
                    },
                }
            );

            console.log("‚úÖ ÌîÑÎ°úÏ†ùÌä∏ ÏÉùÏÑ± ÏÑ±Í≥µ:", response.data);
            setProjects([...projects, response.data]);
            setSelectedProject(response.data);
            localStorage.setItem("selectedProjectId", response.data.id);
            setShowModal(false);
        } catch (error) {
            console.error("‚ùå ÌîÑÎ°úÏ†ùÌä∏ ÏÉùÏÑ± Ïã§Ìå®:", error);
            alert("ÌîÑÎ°úÏ†ùÌä∏ ÏÉùÏÑ±Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.");
        }
    };

    return (
        <div className="main-page">
            <div className="content">
                {projects.length === 0 ? (
                    <div className="empty-gantt">
                        <p>ÌòÑÏû¨ ÌîÑÎ°úÏ†ùÌä∏Í∞Ä ÏóÜÏäµÎãàÎã§.</p>
                        <button className="create-project-btn" onClick={() => setShowModal(true)}>
                            ÏÉàÎ°úÏö¥ ÌîÑÎ°úÏ†ùÌä∏ ÏÉùÏÑ±
                        </button>
                    </div>
                ) : (
                    <GanttChart project={projects[0]} />
                )}
                <TaskBoard />
                <Dashboard tasks={[]} />
            </div>
            <div className="chatbox-container">
                <Sidebar />
                <Chatbox />
            </div>

            {/* üîπ ÌîÑÎ°úÏ†ùÌä∏ ÏÉùÏÑ± Î™®Îã¨ */}
            {showModal && (
                <div className="modal">
                    <div className="modal-content">
                        <h2>ÏÉà ÌîÑÎ°úÏ†ùÌä∏ ÏÉùÏÑ±</h2>
                        <input
                            type="text"
                            placeholder="ÌîÑÎ°úÏ†ùÌä∏ Ïù¥Î¶Ñ"
                            value={projectName}
                            onChange={(e) => setProjectName(e.target.value)}
                        />
                        <button onClick={handleCreateProject}>ÏÉùÏÑ±</button>
                        <button onClick={() => setShowModal(false)}>Ï∑®ÏÜå</button>
                    </div>
                </div>
            )}
        </div>
    );
};

export default MainPage;
