import axios from "axios";
import { getAccessToken } from "../utils/authUtils"; // ‚úÖ accessToken Í∞ÄÏ†∏Ïò§Í∏∞

const API_BASE_URL = "http://localhost:8082/api/auth"; // ‚úÖ Î∞±ÏóîÎìú Í≤ΩÎ°ú ÌôïÏù∏

// ‚úÖ Î™®Îì† ÏöîÏ≤≠Ïóê accessToken ÏûêÎèô Ï∂îÍ∞Ä
const authAxios = axios.create({
	baseURL: API_BASE_URL,
	withCredentials: true, // ‚úÖ Ïø†ÌÇ§ Ìè¨Ìï®ÌïòÏó¨ ÏöîÏ≤≠ (ÌïÑÏàò)
});

authAxios.interceptors.request.use(
	(config) => {
		const token = getAccessToken();
		if (token) {
			config.headers.Authorization = `Bearer ${token}`; // ‚úÖ Authorization Ìó§Îçî Ï∂îÍ∞Ä
		}
		return config;
	},
	(error) => Promise.reject(error)
);

export const signup = async (name, email, password) => {
	try {
		const response = await axios.post("http://localhost:8082/api/auth/register", {
			username: name,
			email,
			password
		}, { withCredentials: true });

		// ‚úÖ Î∞±ÏóîÎìúÏóêÏÑú ÏÑ±Í≥µ ÏùëÎãµ Ïãú message ÌïÑÎìúÍ∞Ä Ï°¥Ïû¨ÌïòÎ©¥ ÏÑ±Í≥µ Ï≤òÎ¶¨
		if (response.status === 200) {
