		} else {
			return { success: false, message: "ÌöåÏõêÍ∞ÄÏûÖ Ïã§Ìå®" };
		}
	} catch (error) {
		return {
			success: false,
			message: error.response?.data || "ÌöåÏõêÍ∞ÄÏûÖ Ïã§Ìå®"
		};
	}
};


