import { Client } from "@stomp/stompjs";

let stompClient = null;
let reconnectAttempts = 0;
const MAX_RECONNECT_ATTEMPTS = 5; // ‚úÖ ÏµúÎåÄ Ïû¨Ïó∞Í≤∞ ÌöüÏàò

export const getStompClient = () => {
    if (stompClient && stompClient.connected) {
        console.log("‚ö†Ô∏è Í∏∞Ï°¥ STOMP ClientÍ∞Ä Ïù¥ÎØ∏ Ïó∞Í≤∞ÎêòÏñ¥ ÏûàÏùå.");
        return stompClient;
    }

    console.log("üü¢ ÏÉàÎ°úÏö¥ STOMP Client ÏÉùÏÑ±");
    stompClient = new Client({
        brokerURL: "ws://localhost:8082/ws",
        connectHeaders: {
            Authorization: `Bearer ${localStorage.getItem("accessToken")}`,
        },
        debug: (msg) => console.log("üìå STOMP Debug:", msg),
        reconnectDelay: 5000,
        heartbeatIncoming: 4000,
        heartbeatOutgoing: 4000,
    });

    stompClient.onConnect = () => {
        console.log("‚úÖ WebSocket Ïó∞Í≤∞ ÏÑ±Í≥µ!");
        reconnectAttempts = 0;
    };

    stompClient.onStompError = (frame) => {
        console.error("üö® STOMP Ïò§Î•ò Î∞úÏÉù:", frame.headers["message"]);
    };

    stompClient.onWebSocketError = (error) => {
        console.error("üö® WebSocket Ïó∞Í≤∞ Ïò§Î•ò:", error);
        reconnectAttempts++;
        if (reconnectAttempts >= MAX_RECONNECT_ATTEMPTS) {
            console.warn("üõë ÏµúÎåÄ Ïû¨Ïó∞Í≤∞ ÏãúÎèÑ Ï¥àÍ≥º. WebSocket Ïû¨Ïó∞Í≤∞ Ï§ëÎã®.");
            stompClient.deactivate();
        }
    };

    stompClient.onDisconnect = () => {
        console.warn("‚ö†Ô∏è WebSocket Ïó∞Í≤∞ Ï¢ÖÎ£åÎê®!");
    };

    return stompClient;
};
