import React, { useState } from 'react';
import '../styles/LoginPage.scss';
import { loginUser, registerUser } from '../api/authApi';

const LoginPage = () => {
  // Î°úÍ∑∏Ïù∏ Îç∞Ïù¥ÌÑ∞
  const [loginData, setLoginData] = useState({ email: "", password: "" });
  const [loginMessage, setLoginMessage] = useState("");

  // ÌöåÏõêÍ∞ÄÏûÖ Îç∞Ïù¥ÌÑ∞
  const [signUpData, setSignUpData] = useState({ username: '', email: '', password: '' });
  const [signUpMessage, setSignUpMessage] = useState("");

  // Î°úÍ∑∏Ïù∏ ÏûÖÎ†• Î≥ÄÍ≤Ω Ìï∏Îì§Îü¨
  const handleLoginChange = (e) => {
    const { name, value } = e.target;
    setLoginData({ ...loginData, [name]: value });
  };

  // ÌöåÏõêÍ∞ÄÏûÖ ÏûÖÎ†• Î≥ÄÍ≤Ω Ìï∏Îì§Îü¨
  const handleSignUpChange = (e) => {
    const { name, value } = e.target;
    setSignUpData({ ...signUpData, [name]: value });
  };

  // Î°úÍ∑∏Ïù∏ Ìèº Ï†úÏ∂ú Ìï∏Îì§Îü¨
  const handleLoginSubmit = async (e) => {
    e.preventDefault();
    try {
      await loginUser(loginData);
      setLoginMessage("Î°úÍ∑∏Ïù∏ ÏÑ±Í≥µ!");
      window.location.href = "/"; // Î°úÍ∑∏Ïù∏ ÌõÑ ÌôàÏúºÎ°ú Ïù¥Îèô
    } catch (error) {
      setLoginMessage(error);
    }
  };

  // ÌöåÏõêÍ∞ÄÏûÖ Ìèº Ï†úÏ∂ú Ìï∏Îì§Îü¨
  const handleSignUpSubmit = async (e) => {
    e.preventDefault();
    console.log("üîµ ÌöåÏõêÍ∞ÄÏûÖ ÏöîÏ≤≠ Î≥¥ÎÉÑ:", signUpData); // ÏΩòÏÜî ÌôïÏù∏

    try {
      const response = await registerUser(signUpData);
      console.log("üü¢ ÌöåÏõêÍ∞ÄÏûÖ ÏÑ±Í≥µ:", response);
      setSignUpMessage("ÌöåÏõêÍ∞ÄÏûÖ ÏÑ±Í≥µ! Î°úÍ∑∏Ïù∏ÌïòÏÑ∏Ïöî.");
    } catch (error) {
      console.error("üî¥ ÌöåÏõêÍ∞ÄÏûÖ Ïã§Ìå®:", error.response ? error.response.data : error.message);
      setSignUpMessage(error.response?.data || " ÌöåÏõêÍ∞ÄÏûÖ Ïã§Ìå®");
    }
  };

  return (
    <div className="login-page">
      <div className="main-container sign-in-mode">
        {/* ÌöåÏõêÍ∞ÄÏûÖ Ìèº */}
        <div className="form-container sign-up-container">
          <form className="form" onSubmit={handleSignUpSubmit}> {/* ‚úÖ ÌöåÏõêÍ∞ÄÏûÖ Ìèº Ï†úÏ∂ú Ï≤òÎ¶¨ */}
            <h1>Create Account</h1>
            <div className="social-container">
              <a href="#" className="social"><i className="fab fa-facebook-f"></i></a>
              <a href="#" className="social"><i className="fab fa-linkedin-in"></i></a>
              <a href="#" className="social"><i className="fab fa-twitter"></i></a>
            </div>
            <span>or use your email for registration</span>
            <input 
              type="text" 
              name="username" 
              placeholder="Username" 
              value={signUpData.username} 
              onChange={handleSignUpChange} 
              required 
            />
            <input 
              type="email" 
              name="email" 
              placeholder="Email" 
              value={signUpData.email} 
              onChange={handleSignUpChange} 
              required 
            />
            <input 
              type="password" 
              name="password" 
              placeholder="Password" 
              value={signUpData.password} 
              onChange={handleSignUpChange} 
              required 
            />
            <button type="submit">Sign Up</button> {/* ‚úÖ ÌöåÏõêÍ∞ÄÏûÖ Î≤ÑÌäº */}
            <p>{signUpMessage}</p> {/* ÌöåÏõêÍ∞ÄÏûÖ Í≤∞Í≥º Î©îÏãúÏßÄ */}
          </form>
        </div>

        {/* Î°úÍ∑∏Ïù∏ Ìèº */}
        <div className="form-container sign-in-container">
          <form className="form" onSubmit={handleLoginSubmit}> {/* ‚úÖ Î°úÍ∑∏Ïù∏ Ìèº Ï†úÏ∂ú Ï≤òÎ¶¨ */}
            <h1>Sign In</h1>
            <input
              type="email"
              name="email"
              placeholder="Email"
              value={loginData.email}
              onChange={handleLoginChange}
              required
            />
            <input
              type="password"
              name="password"
              placeholder="Password"
              value={loginData.password}
              onChange={handleLoginChange}
              required
            />
            <button type="submit">Sign In</button> {/* ‚úÖ Î°úÍ∑∏Ïù∏ Î≤ÑÌäº */}
            <p>{loginMessage}</p> {/* Î°úÍ∑∏Ïù∏ Í≤∞Í≥º Î©îÏãúÏßÄ */}
          </form>
        </div>
      </div>
    </div>
  );
};

export default LoginPage;
