import React, { useState, useEffect } from "react";
import axios from "axios";
import { getAccessToken } from "../utils/authUtils";
import { getStompClient } from "../api/websocket"; // ‚úÖ WebSocket ÌÅ¥ÎùºÏù¥Ïñ∏Ìä∏ Ï∂îÍ∞Ä

const Chatbox = ({ projectId }) => {
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState("");
  const [stompClient, setStompClient] = useState(null); // ‚úÖ WebSocket ÌÅ¥ÎùºÏù¥Ïñ∏Ìä∏ ÏÉÅÌÉú Ï∂îÍ∞Ä

  // ‚úÖ Ï±ÑÌåÖ Î©îÏãúÏßÄ Î∂àÎü¨Ïò§Í∏∞
  const fetchChatMessages = async () => {
    if (!projectId) {
      console.warn("‚ùå ÌîÑÎ°úÏ†ùÌä∏ IDÍ∞Ä Ï†ïÏùòÎêòÏßÄ ÏïäÏùå, Ï±ÑÌåÖ Î∂àÎü¨Ïò§ÏßÄ ÏïäÏùå.");
      return;
    }

    const token = getAccessToken();
    try {
      const response = await axios.get(`http://localhost:8082/api/chat/${projectId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setMessages(response.data);
      console.log("‚úÖ Ï±ÑÌåÖ Î©îÏãúÏßÄ Î°úÎìú ÏÑ±Í≥µ:", response.data);
    } catch (error) {
      console.error("‚ùå Ï±ÑÌåÖ Í∏∞Î°ù Î∂àÎü¨Ïò§Í∏∞ Ïã§Ìå®:", error);
    }
  };

  // ‚úÖ WebSocket Ïó∞Í≤∞ Î∞è Íµ¨ÎèÖ ÏÑ§Ï†ï
  useEffect(() => {
    if (!projectId) return;

    const client = getStompClient();
    setStompClient(client);

    if (!client.connected) {
      console.log("üü¢ WebSocket ÌôúÏÑ±Ìôî...");
      client.activate();
    }

    client.onConnect = () => {
      console.log("‚úÖ WebSocket Ïó∞Í≤∞ ÏÑ±Í≥µ & Ï±ÑÌåÖ Íµ¨ÎèÖ ÏãúÏûë");
      client.subscribe(`/topic/chat/${projectId}`, (message) => {
        const receivedMessage = JSON.parse(message.body);
        console.log("üì© ÏÉà Î©îÏãúÏßÄ ÏàòÏã†:", receivedMessage);
        setMessages((prevMessages) => [...prevMessages, receivedMessage]);
      });
    };

    return () => {
      console.log("üõë WebSocket Ìï¥Ï†ú");
      if (client && client.connected) {
        client.deactivate();
      }
    };
  }, [projectId]);

  // ‚úÖ Î©îÏãúÏßÄ Ï†ÑÏÜ° Ìï®Ïàò
  const sendMessage = async () => {
    if (!newMessage.trim()) return;
    if (!projectId) {
      console.warn("‚ùå ÌîÑÎ°úÏ†ùÌä∏ IDÍ∞Ä ÏóÜÏùå, Î©îÏãúÏßÄÎ•º Ï†ÑÏÜ°Ìï† Ïàò ÏóÜÏùå.");
      return;
    }

    const token = getAccessToken();
    const messageData = {
      projectId,
      content: newMessage,
    };

    try {
      await axios.post("http://localhost:8082/api/chat/send", messageData, {
        headers: { Authorization: `Bearer ${token}` },
      });

      console.log("üì§ Î©îÏãúÏßÄ Ï†ÑÏÜ°:", newMessage);
      setNewMessage("");
    } catch (error) {
      console.error("‚ùå Î©îÏãúÏßÄ Ï†ÑÏÜ° Ïã§Ìå®:", error);
    }
  };

  // ‚úÖ ÌîÑÎ°úÏ†ùÌä∏ IDÍ∞Ä ÏóÜÏùÑ Í≤ΩÏö∞, UI Î†åÎçîÎßÅ Î∞©ÏßÄ
  if (!projectId) {
    return <p className="chatbox-warning">üö® ÌîÑÎ°úÏ†ùÌä∏Î•º ÏÑ†ÌÉùÌï¥Ï£ºÏÑ∏Ïöî.</p>;
  }

  return (
    <div className="chatbox-container">
      <h3>ÌåÄ Ï±ÑÌåÖ</h3>
      <div className="messages">
        {messages.length > 0 ? (
          messages.map((msg, index) => (
            <p key={index}><strong>{msg.sender.username}</strong>: {msg.content}</p>
          ))
        ) : (
          <p>üì≠ ÏïÑÏßÅ Î©îÏãúÏßÄÍ∞Ä ÏóÜÏäµÎãàÎã§.</p>
        )}
      </div>
      <input
        type="text"
        value={newMessage}
        onChange={(e) => setNewMessage(e.target.value)}
        placeholder="Î©îÏãúÏßÄÎ•º ÏûÖÎ†•ÌïòÏÑ∏Ïöî..."
      />
      <button onClick={sendMessage}>Ï†ÑÏÜ°</button>
    </div>
  );
};

export default Chatbox;
