import React, { useState, useEffect } from "react";
import axios from "axios";
import "../styles/TaskModal.css";
import { getAccessToken } from "../utils/authUtils";

const TaskModal = ({ onClose, projectId, refreshTasks, editTask }) => {
  // ‚úÖ ÏàòÏ†ï Î™®ÎìúÏù∏ÏßÄ Ïó¨Î∂Ä Ï≤¥ÌÅ¨
  const isEditMode = !!editTask; // editTaskÍ∞Ä Ï°¥Ïû¨ÌïòÎ©¥ ÏàòÏ†ï Î™®Îìú

  // ‚úÖ Ï¥àÍ∏∞Í∞í ÏÑ§Ï†ï (ÏàòÏ†ï Î™®ÎìúÎ©¥ Í∏∞Ï°¥ Í∞í ÏÇ¨Ïö©)
  const [taskData, setTaskData] = useState({
    name: editTask ? editTask.name : "",
    assignedTo: editTask ? editTask.assignedTo : "",
    startDate: editTask ? editTask.startDate : "",
    dueDate: editTask ? editTask.dueDate : "",
    description: editTask ? editTask.description : "",
    status: editTask ? editTask.status : "TODO",
    color: editTask ? editTask.color : "#ff99a5", // ‚úÖ ÏÉâÏÉÅ ÌïÑÎìú Ï∂îÍ∞Ä

  });

  // ‚úÖ Task ÏàòÏ†ï Ïãú Í∏∞Ï°¥ Îç∞Ïù¥ÌÑ∞Î•º Î∂àÎü¨ÏôÄÏÑú ÏÉÅÌÉú ÏóÖÎç∞Ïù¥Ìä∏
  useEffect(() => {
    if (editTask) {
      setTaskData({
        name: editTask.name,
        assignedTo: editTask.assignedTo,
        startDate: editTask.startDate,
        dueDate: editTask.dueDate,
        description: editTask.description,
        status: editTask.status,
        color: editTask.color || "#ff99a5", // ‚úÖ Í∏∞Ï°¥Ïóê ÏÉâÏÉÅÏù¥ ÏóÜÏúºÎ©¥ Í∏∞Î≥∏Í∞í ÏÇ¨Ïö©
      });
    }
  }, [editTask]);

  // ‚úÖ ÏûÖÎ†•Í∞íÏù¥ Î≥ÄÍ≤ΩÎê† Îïå ÏÉÅÌÉú ÏóÖÎç∞Ïù¥Ìä∏
  const handleChange = (e) => {
    setTaskData({ ...taskData, [e.target.name]: e.target.value });
  };

  // ‚úÖ ÏÉâÏÉÅ Î≥ÄÍ≤Ω Ìï∏Îì§Îü¨
  const handleColorChange = (e) => {
    const newColor = e.target.value;
    console.log("üé® ÏÑ†ÌÉùÌïú ÏÉâÏÉÅ:", newColor); // ‚úÖ ÏΩòÏÜîÏóêÏÑú ÏÑ†ÌÉùÎêú ÏÉâÏÉÅ ÌôïÏù∏
    setTaskData({ ...taskData, color: newColor }); // ‚úÖ taskDataÏóê color Ï†ÄÏû•
  };



  // ‚úÖ Task Ï†ÄÏû• (ÏàòÏ†ï ÎòêÎäî ÏÉùÏÑ±)
  const handleSubmit = async () => {
    console.log("üöÄ Ï†ÄÏû• ÏöîÏ≤≠ Îç∞Ïù¥ÌÑ∞:", taskData); // Ï†ÄÏû•Ìï† Îç∞Ïù¥ÌÑ∞ ÌôïÏù∏
    const token = getAccessToken();
    if (!token) {
      alert("Î°úÍ∑∏Ïù∏Ïù¥ ÌïÑÏöîÌï©ÎãàÎã§.");
      return;
    }

    try {
      if (isEditMode) {
        // ‚úÖ ÏàòÏ†ï API Ìò∏Ï∂ú
        await axios.put(`http://localhost:8082/api/user/tasks/${editTask.id}`, 
          { ...taskData, color: taskData.color, projectId }, // ‚úÖ color Ìè¨Ìï®
          {
            headers: {
              Authorization: `Bearer ${token}`,
              "Content-Type": "application/json",
            },
            withCredentials: true,
          }
        );
        alert("ÏóÖÎ¨¥Í∞Ä ÏÑ±Í≥µÏ†ÅÏúºÎ°ú ÏàòÏ†ïÎêòÏóàÏäµÎãàÎã§!");
      } else {
        // ‚úÖ ÏÉùÏÑ± API Ìò∏Ï∂ú
        await axios.post("http://localhost:8082/api/user/tasks", 
          { ...taskData, color: taskData.color, projectId }, // ‚úÖ color Ìè¨Ìï®
          {
            headers: {
              Authorization: `Bearer ${token}`,
              "Content-Type": "application/json",
            },
            withCredentials: true,
          }
        );
        alert("ÏóÖÎ¨¥Í∞Ä ÏÑ±Í≥µÏ†ÅÏúºÎ°ú Îì±Î°ùÎêòÏóàÏäµÎãàÎã§!");
      }

      refreshTasks(); // ‚úÖ Task Î™©Î°ù Í∞±Ïã†
      onClose(); // ‚úÖ Î™®Îã¨ Îã´Í∏∞
    } catch (error) {
      console.error("‚ùå Task Ï†ÄÏû• Ïã§Ìå®:", error);
      alert("ÏóÖÎ¨¥ Ï†ÄÏû•Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.");
    }
  };

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <h2>{isEditMode ? "ÏóÖÎ¨¥ ÏàòÏ†ï" : "ÏóÖÎ¨¥ Ï∂îÍ∞Ä"}</h2>

        {/* ‚úÖ ÏóÖÎ¨¥ Ï†úÎ™© ÏûÖÎ†• */}
        <label>ÏóÖÎ¨¥ Ï†úÎ™©:</label>
        <input type="text" name="name" value={taskData.name} onChange={handleChange} placeholder="ÏóÖÎ¨¥ Ï†úÎ™© ÏûÖÎ†•" />

        {/* ‚úÖ Îã¥ÎãπÏûê ÏÑ†ÌÉù */}
        <label>Îã¥ÎãπÏûê:</label>
        <select name="assignedTo" value={taskData.assignedTo} onChange={handleChange}>
          <option value="">Îã¥ÎãπÏûê ÏÑ†ÌÉù</option>
          <option value="1">ÏÇ¨Ïö©Ïûê 1</option>
          <option value="2">ÏÇ¨Ïö©Ïûê 2</option>
        </select>

        {/* ‚úÖ ÏûëÏóÖ ÏãúÏûëÏùº ÏÑ†ÌÉù */}
        <label>ÏûëÏóÖ ÏãúÏûëÏùº:</label>
        <input type="date" name="startDate" value={taskData.startDate} onChange={handleChange} />

        {/* ‚úÖ ÏûëÏóÖ ÎßàÍ∞êÏùº ÏÑ†ÌÉù */}
        <label>ÏûëÏóÖ ÎßàÍ∞êÏùº:</label>
        <input type="date" name="dueDate" value={taskData.dueDate} onChange={handleChange} />

        {/* ‚úÖ ÏóÖÎ¨¥ ÏÉÅÌÉú ÏÑ†ÌÉù */}
        <label>ÏóÖÎ¨¥ ÏÉÅÌÉú:</label>
        <select name="status" value={taskData.status} onChange={handleChange}>
          <option value="TODO">Ìï† Ïùº</option>
          <option value="IN_PROGRESS">ÏßÑÌñâ Ï§ë</option>
          <option value="DONE">ÏôÑÎ£å</option>
        </select>

        {/* ‚úÖ ÏûëÏóÖ ÎÇ¥Ïö© ÏûÖÎ†• */}
        <label>ÏûëÏóÖ ÎÇ¥Ïö©:</label>
        <textarea name="description" value={taskData.description} onChange={handleChange} placeholder="ÏóÖÎ¨¥ ÎÇ¥Ïö©ÏùÑ ÏûÖÎ†•ÌïòÏÑ∏Ïöî." />

        {/* ‚úÖ ÏÉâÏÉÅ ÏÑ†ÌÉù Í∏∞Îä• */}
        <label>ÏÉâÏÉÅ ÏÑ†ÌÉù</label>
        <input type="color" name="color" value={taskData.color} onChange={handleColorChange} />

        {/* ‚úÖ Î≤ÑÌäº ÏòÅÏó≠ */}
        <div className="modal-actions">
          <button onClick={onClose}>Ï∑®ÏÜå</button>
          <button onClick={handleSubmit} disabled={!taskData.name || !taskData.assignedTo || !taskData.startDate || !taskData.dueDate || !taskData.description || !taskData.status}>
            {isEditMode ? "ÏàòÏ†ï" : "Îì±Î°ù"}
          </button>
        </div>
      </div>
    </div>
  );
};

export default TaskModal;
