import React, { useState, useEffect } from 'react';
import '../styles/GanttChart.css';

// ‚úÖ ÏàòÏ†ï: Task Îç∞Ïù¥ÌÑ∞Î•º propsÎ°ú Î∞õÏïÑÏÑú ÏÇ¨Ïö©ÌïòÎèÑÎ°ù Î≥ÄÍ≤Ω
const GanttChart = ({ tasks }) => {
  const [viewMode, setViewMode] = useState('week'); // ‚úÖ ÌòÑÏû¨ Î≥¥Í∏∞ Î™®Îìú (week, month, year)
  const [currentWeekStart, setCurrentWeekStart] = useState(getStartOfWeek(new Date()));
  const [currentMonth, setCurrentMonth] = useState(new Date());
  const [currentYear, setCurrentYear] = useState(new Date().getFullYear());
  const today = new Date();

  // ‚úÖ ÏÇ≠Ï†ú: Í∏∞Ï°¥ ÎçîÎØ∏ Îç∞Ïù¥ÌÑ∞ ÏÇ≠Ï†ú ÌïÑÏöî
  // const initialTasks = [
  //   { id: 1, name: 'Design Phase', start: new Date(2025, 1, 5), end: new Date(2025, 1, 8), progress: 100 },
  //   { id: 2, name: 'Development Phase', start: new Date(2025, 1, 5), end: new Date(2025, 1, 9), progress: 75 },
  //   { id: 3, name: 'Testing Phase', start: new Date(2025, 1, 5), end: new Date(2025, 1, 10), progress: 30 },
  // ];

  const isTaskOnDate = (date, task) => {
    const taskStart = new Date(task.start).setHours(0, 0, 0, 0);
    const taskEnd = new Date(task.end).setHours(23, 59, 59, 999);
    const checkDate = new Date(date).setHours(12, 0, 0, 0); // ‚úÖ ÎÇ†Ïßú Ï§ëÏïôÏúºÎ°ú ÏÑ§Ï†ï (ÎπÑÍµê Ïò§Î•ò Î∞©ÏßÄ)
  
    return checkDate >= taskStart && checkDate <= taskEnd;
  };

  // Task Îç∞Ïù¥ÌÑ∞Î•º Î∞õÏïÑÏÑú Gantt Ï∞®Ìä∏Ïóê Î∞òÏòÅ
  const formattedTasks = tasks.map((task) => ({
    id: task.id,
    name: task.name,
    start: new Date(task.startDate),
    end: new Date(new Date(task.dueDate).setHours(23, 59, 59, 999)), // ‚úÖ ÎßàÍ∞êÏùº 23:59:59Î°ú ÏÑ§Ï†ï
    progress: 50, // ‚úÖ ÏßÑÌñâÎ•† Í∏∞Î≥∏Í∞í (Ï∂îÌõÑ Î∞±ÏóîÎìúÏóêÏÑú Í∞ÄÏ†∏Ïò§ÎèÑÎ°ù ÏàòÏ†ï Í∞ÄÎä•)
  }));

  // ‚úÖ ÌòÑÏû¨ Ï£ºÏùò ÏãúÏûë ÎÇ†Ïßú Í≥ÑÏÇ∞
  function getStartOfWeek(date) {
    const start = new Date(date);
    start.setDate(start.getDate() - start.getDay()); // ÏùºÏöîÏùºÎ°ú ÏÑ§Ï†ï
    start.setHours(0, 0, 0, 0);
    return start;
  }

  // ‚úÖ Ìïú Ï£ºÏùò ÎÇ†Ïßú Í∞ÄÏ†∏Ïò§Í∏∞
  // const getWeekDates = () => {
  //   const today = new Date();
  //   const firstDayOfWeek = today.getDate() - today.getDay();
  //   return Array.from({ length: 7 }, (_, i) => {
  //     const date = new Date(today.getFullYear(), today.getMonth(), firstDayOfWeek + i);
  //     return {
  //       day: date.toLocaleDateString('en-US', { weekday: 'short' }),
  //       date: new Date(date.getFullYear(), date.getMonth(), date.getDate(), 0, 0, 0),
  //     };
  //   });
  // };
  const getWeekDates = () => {
    return Array.from({ length: 7 }, (_, i) => {
      const date = new Date(currentWeekStart);
      date.setDate(currentWeekStart.getDate() + i);
      return {
        day: date.toLocaleDateString('en-US', { weekday: 'short' }),
        date: new Date(date.getFullYear(), date.getMonth(), date.getDate(), 0, 0, 0),
      };
    });
  };

  // ‚úÖ Ïù¥Ï†Ñ Ï£º Î≥¥Í∏∞
  const handlePreviousWeek = () => {
    const prevWeek = new Date(currentWeekStart);
    prevWeek.setDate(prevWeek.getDate() - 7);
    setCurrentWeekStart(prevWeek);
  };

  // ‚úÖ Îã§Ïùå Ï£º Î≥¥Í∏∞
  const handleNextWeek = () => {
    const nextWeek = new Date(currentWeekStart);
    nextWeek.setDate(nextWeek.getDate() + 7);
    setCurrentWeekStart(nextWeek);
  };

  const getMonthDates = () => {
    const firstDayOfMonth = new Date(currentMonth.getFullYear(), currentMonth.getMonth(), 1);
    const lastDayOfMonth = new Date(currentMonth.getFullYear(), currentMonth.getMonth() + 1, 0);
    const dates = [];

    for (let i = 0; i < firstDayOfMonth.getDay(); i++) {
      dates.push(null);
    }

    for (let i = 1; i <= lastDayOfMonth.getDate(); i++) {
      const date = new Date(currentMonth.getFullYear(), currentMonth.getMonth(), i);
      dates.push(date);
    }

    return dates;
  };

  const handlePreviousMonth = () => {
    setCurrentMonth(new Date(currentMonth.getFullYear(), currentMonth.getMonth() - 1, 1));
  };

  const handleNextMonth = () => {
    setCurrentMonth(new Date(currentMonth.getFullYear(), currentMonth.getMonth() + 1, 1));
  };

  const getYearMonths = () => {
    return Array.from({ length: 12 }, (_, index) => {
      const monthDate = new Date(currentYear, index, 1);
      return {
        name: monthDate.toLocaleDateString('en-US', { month: 'short' }).toUpperCase(),
        date: monthDate,
      };
    });
  };

  // ‚úÖ Ï£ºÏñ¥ÏßÑ TaskÏùò ÏúÑÏπòÏôÄ Í∏∏Ïù¥Î•º Í≥ÑÏÇ∞ÌïòÏó¨ Ìïú Ï£º ÏïàÏóêÏÑú ÌëúÏãú
  // const calculateProgressBarStyle = (task) => {
  //   const totalDays = 7;
  //   const weekStart = new Date();
  //   weekStart.setDate(weekStart.getDate() - weekStart.getDay());

  //   const startOffset = Math.max(0, (task.start - weekStart) / (1000 * 60 * 60 * 24));
  //   const duration = Math.max(1, (task.end - task.start) / (1000 * 60 * 60 * 24));

  //   return {
  //     left: `${(startOffset / totalDays) * 100}%`,
  //     width: `${(duration / totalDays) * 100}%`,
  //   };
  // };
  const calculateProgressBarStyle = (task) => {
    const weekStart = currentWeekStart.getTime();
    const weekEnd = new Date(weekStart);
    weekEnd.setDate(weekEnd.getDate() + 7);

    const taskStart = Math.max(task.start.getTime(), weekStart);
    const taskEnd = Math.min(task.end.getTime(), weekEnd);

    if (taskEnd < weekStart || taskStart > weekEnd) {
      return { display: 'none' };
    }

    const totalWeekMs = weekEnd - weekStart;
    const offset = ((taskStart - weekStart) / totalWeekMs) * 100;
    const width = ((taskEnd - taskStart) / totalWeekMs) * 100;

    return {
      left: `${offset}%`,
      width: `${width}%`,
    };
  };

  return (
    <div className="gantt-chart">
      <h2 className="project-title">üìä ÏóÖÎ¨¥ ÏùºÏ†ï (Gantt Chart)</h2>

      {viewMode === 'week' && (
        <>
          <div className="week-navigation">
            <button onClick={handlePreviousWeek}>&lt;</button>
            <span>{currentWeekStart.toLocaleDateString('en-US', { month: 'long', day: 'numeric' })} ~ {new Date(currentWeekStart.getTime() + 6 * 24 * 60 * 60 * 1000).toLocaleDateString('en-US', { month: 'long', day: 'numeric' })}</span>
            <button onClick={handleNextWeek}>&gt;</button>
          </div>

          <div className="calendar">
            {getWeekDates().map((day, index) => (
              <div key={index} className="calendar-day">
                <div className="day-name">{day.day}</div>
                <div className="day-date">{day.date.toLocaleDateString('en-US', { day: 'numeric', month: 'short' })}</div>
              </div>
            ))}
          </div>

          <div className="task-container">
            {formattedTasks.map((task) => {
              const progressBarStyle = calculateProgressBarStyle(task);
              return (
                <div key={task.id} className="task">
                  <div className="progress-bar" style={progressBarStyle}>
                    <div className="progress">
                      <span className="task-name">{task.name}</span>
                    </div>
                  </div>
                </div>
              );
            })}
          </div>
        </>
      )}

      {viewMode === 'month' && (
        <div className="month-calendar">
          <div className="month-header">
            <button className="month-nav" onClick={handlePreviousMonth}>&lt;</button>
            <div className="month-title">{currentMonth.toLocaleDateString('en-US', { month: 'long', year: 'numeric' })}</div>
            <button className="month-nav" onClick={handleNextMonth}>&gt;</button>
          </div>
          {/* <div className="calendar-grid">
            {getMonthDates().map((date, index) => (
              <div key={index} className="calendar-day">
                {date ? date.getDate() : ""}</div>
            ))}
          </div> */}
          <div className="calendar-grid">
            {getMonthDates().map((date, index) => (
              <div key={index} className="calendar-day">
                {date && (
                  <>
                    <div className="day-date">{date.getDate()}</div>
                    <div className="task-indicator-container">
                      {tasks.map(
                        (task) =>
                          isTaskOnDate(date, task) && (
                            <div key={task.id} className="task-indicator task-bar"
                              style={{ backgroundColor: task.color }}></div>
                          )
                      )}
                    </div>
                  </>
                )}
              </div>
            ))}
          </div>
        </div>
      )}

      <div className="view-mode-buttons">
        <button className={`view-button ${viewMode === 'week' ? 'active' : ''}`} onClick={() => setViewMode('week')}>Week</button>
        <button className={`view-button ${viewMode === 'month' ? 'active' : ''}`} onClick={() => setViewMode('month')}>Month</button>
      </div>
    </div>
  );
};

export default GanttChart;
