import React, { useState, useEffect } from "react";
import Sidebar from "../components/Sidebar";
import GanttChart from "../components/GanttChart";
import Chatbox from "../components/Chatbox";
import Dashboard from "../components/Dashboard";
import TaskBoard from "../components/TaskBoard";
import "../styles/MainPage.css";
import axios from "axios";
import { useNavigate, useParams } from "react-router-dom";
import { getAccessToken } from "../utils/authUtils";
import InviteList from "../components/InviteList"; // ‚úÖ Ï¥àÎåÄ Î™©Î°ù Ïª¥Ìè¨ÎÑåÌä∏ Ï∂îÍ∞Ä
import { getStompClient } from "../api/websocket"; // ‚úÖ getStompClient ÏÇ¨Ïö©



const MainPage = () => {
    const [projects, setProjects] = useState([]); // ‚úÖ ÌîÑÎ°úÏ†ùÌä∏ Î™©Î°ù Ï†ÄÏû•
    const [selectedProject, setSelectedProject] = useState(null); // ‚úÖ ÏÑ†ÌÉùÌïú ÌîÑÎ°úÏ†ùÌä∏ Ï†ÄÏû•
    const [tasks, setTasks] = useState([]); // ‚úÖ ÏÑ†ÌÉùÌïú ÌîÑÎ°úÏ†ùÌä∏Ïùò ÏûëÏóÖ Î™©Î°ù
    const [showModal, setShowModal] = useState(false);
    const [projectName, setProjectName] = useState("");
    const [user, setUser] = useState(null);
    const { userId } = useParams();
    const navigate = useNavigate(); // ‚úÖ ÌéòÏù¥ÏßÄ Ïù¥Îèô
    const [projectDescription, setProjectDescription] = useState(""); // ‚úÖ ÏÑ§Î™Ö Ï∂îÍ∞Ä
    const [invites, setInvites] = useState([]); // ‚úÖ Ï¥àÎåÄ Î™©Î°ù ÏÉÅÌÉú Ï∂îÍ∞Ä

    // ‚úÖ Î°úÍ∑∏Ïù∏Ìïú ÏÇ¨Ïö©ÏûêÏùò ÌîÑÎ°úÏ†ùÌä∏ Î™©Î°ù Î∂àÎü¨Ïò§Í∏∞
    const fetchProjects = async () => {
        const token = getAccessToken();
        if (!token) {
            alert("Î°úÍ∑∏Ïù∏Ïù¥ ÌïÑÏöîÌï©ÎãàÎã§.");
            navigate("/login");
            return;
        }

        try {
            const response = await axios.get("http://localhost:8082/api/user/projects", {
                headers: {
                    Authorization: `Bearer ${token}`,
                    "Content-Type": "application/json",
                },
                withCredentials: true,
            });

            console.log("‚úÖ ÏÑúÎ≤ÑÏóêÏÑú Í∞ÄÏ†∏Ïò® ÌîÑÎ°úÏ†ùÌä∏ Î™©Î°ù:", response.data);
            if (response.data && response.data.length > 0) {
                const formattedProjects = response.data.map(project => ({
                    ...project,
                    name: project.name ? project.name : "üö® Ïù¥Î¶Ñ ÏóÜÏùå",
                }));
                setProjects(formattedProjects);

                // üîπ Ï≤´ Î≤àÏß∏ ÌîÑÎ°úÏ†ùÌä∏Î•º ÏûêÎèôÏúºÎ°ú ÏÑ†ÌÉù
                setSelectedProject(formattedProjects[0]);
                fetchTasks(formattedProjects[0].id);
            }
        } catch (error) {
            console.error("‚ùå ÌîÑÎ°úÏ†ùÌä∏ Î™©Î°ù Î∂àÎü¨Ïò§Í∏∞ Ïã§Ìå®:", error);
            if (error.response?.status === 401) {
                localStorage.removeItem("accessToken");
                sessionStorage.removeItem("accessToken");
                navigate("/login");
            }
        }
    };




    // ÏÉàÎ°úÏö¥ Access Token Î∞úÍ∏â
    const refreshAccessToken = async () => {
        const refreshToken = localStorage.getItem("refreshToken");
        if (!refreshToken) {
            navigate("/login");
            return;
        }

        try {
            const response = await axios.post("http://localhost:8082/api/auth/refresh", {
                refreshToken,
            });

            localStorage.setItem("accessToken", response.data.accessToken);
            sessionStorage.setItem("accessToken", response.data.accessToken); // ‚úÖ Ï∂îÍ∞Ä: sessionStorageÏóêÎèÑ Ï†ÄÏû•
            console.log("‚úÖ ÏÉà Access Token Î∞úÍ∏â:", response.data.accessToken);
