import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { handleLogout } from "../utils/authUtils"; // ‚úÖ Î°úÍ∑∏ÏïÑÏõÉ Ìï®Ïàò ÏÇ¨Ïö©

const SessionTimeout = () => {
    const navigate = useNavigate();
    const [showWarning, setShowWarning] = useState(false); // üîπ Í≤ΩÍ≥†Ï∞Ω ÏÉÅÌÉú Ï∂îÍ∞Ä
    const [logoutCountdown, setLogoutCountdown] = useState(5 * 60); // üîπ Î°úÍ∑∏ÏïÑÏõÉÍπåÏßÄ ÎÇ®ÏùÄ ÏãúÍ∞Ñ (5Î∂Ñ)
    const [showLogoutMessage, setShowLogoutMessage] = useState(false); // üîπ ÏûêÎèô Î°úÍ∑∏ÏïÑÏõÉ Î©îÏãúÏßÄ ÌëúÏãú Ïó¨Î∂Ä
    const warningTimeout = 55 * 60 * 1000; // 55Î∂Ñ ÌõÑ Í≤ΩÍ≥†Ï∞Ω ÌëúÏãú
    const logoutTimeout = warningTimeout + 5 * 60 * 1000; // üîπ Í≤ΩÍ≥†Ï∞Ω ÌõÑ 5Î∂Ñ ÎÇ¥ ÏùëÎãµ ÏóÜÏúºÎ©¥ ÏûêÎèô Î°úÍ∑∏ÏïÑÏõÉ

    useEffect(() => {
        // üîπ 55Î∂Ñ ÌõÑ Í≤ΩÍ≥†Ï∞Ω ÌëúÏãú
        const warningTimer = setTimeout(() => {
            setShowWarning(true);
        }, warningTimeout);

        // üîπ 60Î∂Ñ ÌõÑ ÏûêÎèô Î°úÍ∑∏ÏïÑÏõÉ
        const logoutTimer = setTimeout(() => {
            if (showWarning) {
                handleLogout();
                setShowLogoutMessage(true); // ÏûêÎèô Î°úÍ∑∏ÏïÑÏõÉ Î©îÏãúÏßÄ ÌëúÏãú
                setTimeout(() => navigate("/login"), 2000); // 2Ï¥à ÌõÑ Î°úÍ∑∏Ïù∏ ÌéòÏù¥ÏßÄÎ°ú Ïù¥Îèô
            }
        }, logoutTimeout);

        // üîπ Í≤ΩÍ≥†Ï∞ΩÏù¥ ÌëúÏãúÎêòÎ©¥ ÎÇ®ÏùÄ ÏãúÍ∞Ñ Í∞êÏÜå ÏãúÏûë
        let countdownInterval;
        if (showWarning) {
            countdownInterval = setInterval(() => {
                setLogoutCountdown((prev) => (prev > 0 ? prev - 1 : 0));
            }, 1000);
        }

        return () => {
            clearTimeout(warningTimer);
            clearTimeout(logoutTimer);
            clearInterval(countdownInterval);
        };
    }, [showWarning, navigate]);

    // üîπ ÏÇ¨Ïö©ÏûêÍ∞Ä "ÌôïÏù∏" Î≤ÑÌäºÏùÑ ÎàÑÎ•¥Î©¥ ÌÜ†ÌÅ∞ Í∞±Ïã†
    const handleRefreshToken = async () => {
        setShowWarning(false); // Í≤ΩÍ≥†Ï∞Ω Îã´Í∏∞
        setLogoutCountdown(5 * 60); // ÌÉÄÏù¥Î®∏ Ï¥àÍ∏∞Ìôî
        try {
            const refreshToken = localStorage.getItem("refreshToken");
            const response = await axios.post("/api/auth/refresh", { refreshToken });

            localStorage.setItem("accessToken", response.data.accessToken);
            sessionStorage.setItem("accessToken", response.data.accessToken);
            alert("ÏÑ∏ÏÖòÏù¥ Ïó∞Ïû•ÎêòÏóàÏäµÎãàÎã§.");
        } catch (error) {
            handleLogout();
            setShowLogoutMessage(true);
            setTimeout(() => navigate("/login"), 2000);
        }
    };

    return (
        <>
            {showWarning && (
                <div className="session-warning">
                    <p>Î°úÍ∑∏Ïù∏ÏùÑ Ïú†ÏßÄÌï†ÍπåÏöî?</p>
                    <p>ÎÇ®ÏùÄ ÏãúÍ∞Ñ: {Math.floor(logoutCountdown / 60)}Î∂Ñ {logoutCountdown % 60}Ï¥à</p>
                    <button onClick={handleRefreshToken}>ÌôïÏù∏</button>
                </div>
            )}

            {showLogoutMessage && (
                <div className="session-logout-message">
                    <p>ÏûêÎèô Î°úÍ∑∏ÏïÑÏõÉÎêòÏóàÏäµÎãàÎã§.</p>
                </div>
            )}
        </>
    );
};

export default SessionTimeout;
