            debug: (msg) => console.log("ðŸ“Œ STOMP Debug:", msg),
            reconnectDelay: 5000, // âœ… 5ì´ˆ í›„ ìžë™ ìž¬ì—°ê²°
            heartbeatIncoming: 4000,
            heartbeatOutgoing: 4000,
        });

        stompClient.onConnect = () => console.log("âœ… WebSocket ì—°ê²° ì„±ê³µ!");
        stompClient.onStompError = (frame) => console.error("ðŸš¨ STOMP ì˜¤ë¥˜ ë°œìƒ:", frame.headers["message"]);
        stompClient.onWebSocketError = (error) => console.error("ðŸš¨ WebSocket ì—°ê²° ì˜¤ë¥˜:", error);
    }
    return stompClient;
};
