import React, { useEffect, useState } from "react";
import axios from "axios";
import { getAccessToken } from "../utils/authUtils";
import { getStompClient } from "../api/websocket"; // ‚úÖ WebSocket ÌÅ¥ÎùºÏù¥Ïñ∏Ìä∏ Í∞ÄÏ†∏Ïò§Í∏∞
import "../styles/InviteList.css"; 

const InviteList = ({ refreshProjects }) => {
  const [invites, setInvites] = useState([]);
  const [notifications, setNotifications] = useState([]);

  const fetchInvites = async () => {
    const token = getAccessToken();
    try {
        const response = await axios.get("http://localhost:8082/api/team/invites", {
            headers: { Authorization: `Bearer ${token}` },
        });
        console.log("üìå Î∞õÏùÄ Ï¥àÎåÄ Î™©Î°ù:", response.data);
        setInvites(response.data);
    } catch (error) {
        console.error("‚ùå Ï¥àÎåÄ Î™©Î°ù Î∂àÎü¨Ïò§Í∏∞ Ïã§Ìå®:", error);
    }
};
useEffect(() => {
    fetchInvites(); // Ï¥àÎåÄ Î™©Î°ù Î∂àÎü¨Ïò§Í∏∞

    const stompClient = getStompClient();
    if (stompClient.connected) {
      console.log("‚úÖ Ï¥àÎåÄ ÏïåÎ¶º WebSocket Ïù¥ÎØ∏ Ïó∞Í≤∞Îê®.");
    } else {
      stompClient.onConnect = () => {
        console.log("‚úÖ WebSocket Ïó∞Í≤∞ ÏÑ±Í≥µ & Ï¥àÎåÄ ÏïåÎ¶º Íµ¨ÎèÖ ÏãúÏûë");
        stompClient.subscribe("/topic/invites", (message) => {
          try {
            const newInvite = JSON.parse(message.body);
            console.log("üì© ÏÉà Ï¥àÎåÄ ÏïåÎ¶º ÏàòÏã†:", newInvite);

            // üîπ PENDING ÏÉÅÌÉúÏùò Ï¥àÎåÄÎßå ÌïÑÌÑ∞ÎßÅÌïòÏó¨ Ï∂îÍ∞Ä
            if (newInvite.status === "PENDING") {
              setNotifications((prev) => [...prev, newInvite]);
            }

            // üîπ Ï¥àÎåÄ Î™©Î°ùÎèÑ Ï¶âÏãú Í∞±Ïã†
            fetchInvites();
          } catch (error) {
            console.error("‚ùå ÏïåÎ¶º JSON ÌååÏã± Ïã§Ìå®:", error);
          }
        });
      };
    }

    return () => {
      console.log("üõë WebSocket Ìï¥Ï†ú");
      if (stompClient && stompClient.connected) {
        stompClient.deactivate();
      }
    };
  }, []);



  return (
    <>
      {notifications.length > 0 ? ( // üîπ notificationsÍ∞Ä ÏûàÏùÑ ÎïåÎßå Î†åÎçîÎßÅ
        <div className="invite-popup-container">
          {notifications.map((invite) => (
            <div key={invite.id} className="invite-popup">
              <p>
                <strong>{invite.sender.username}</strong> ÎãòÏù¥{" "}
                <strong>{invite.project.name}</strong> ÌîÑÎ°úÏ†ùÌä∏Ïóê Ï¥àÎåÄÌñàÏäµÎãàÎã§.
              </p>
              <button onClick={() => setNotifications([])}>‚úÖ ÌôïÏù∏</button>
            </div>
          ))}
        </div>
      ) : null} {/* üîπ ÏïåÎ¶ºÏù¥ ÏóÜÏúºÎ©¥ ÏïÑÎ¨¥Í≤ÉÎèÑ ÌëúÏãú Ïïà Ìï® */}
    </>
  );
};

export default InviteList;
