import React, { useEffect, useState } from "react";
import axios from "axios";
import { getAccessToken } from "../utils/authUtils";
import "../styles/InviteList.css"; 

const InviteList = ({ refreshProjects }) => {
  const [invites, setInvites] = useState([]);
  const [showPopup, setShowPopup] = useState(false); // ‚úÖ ÌåùÏóÖ ÏÉÅÌÉú Ï∂îÍ∞Ä

  // ‚úÖ Ï¥àÎåÄ Î™©Î°ù Í∞ÄÏ†∏Ïò§Í∏∞
  const fetchInvites = async () => {
    const token = getAccessToken();
    try {
      const response = await axios.get("http://localhost:8082/api/team/invites", {
        headers: { Authorization: `Bearer ${token}` },
      });
      console.log("üìå Î∞õÏùÄ Ï¥àÎåÄ Î™©Î°ù:", response.data);
      setInvites(response.data);
      setShowPopup(response.data.length > 0); // Ï¥àÎåÄÍ∞Ä ÏûàÏúºÎ©¥ ÌåùÏóÖ ÌëúÏãú
    } catch (error) {
      console.error("‚ùå Ï¥àÎåÄ Î™©Î°ù Î∂àÎü¨Ïò§Í∏∞ Ïã§Ìå®:", error);
    }
  };

  useEffect(() => {
    fetchInvites();
  }, []);

  // ‚úÖ Ï¥àÎåÄ ÏàòÎùΩ Ï≤òÎ¶¨ (UI Ï¶âÏãú ÏóÖÎç∞Ïù¥Ìä∏)
  const handleAcceptInvite = async (inviteId) => {
    const token = getAccessToken();
    try {
      await axios.post(`http://localhost:8082/api/team/invite/${inviteId}/accept`, {}, {
        headers: { Authorization: `Bearer ${token}` },
      });
      console.log(`‚úÖ Ï¥àÎåÄ(${inviteId}) ÏàòÎùΩ`);

      // ‚úÖ UIÏóêÏÑú Ï¥àÎåÄ Ï†úÍ±∞
      setInvites((prevInvites) => {
        const updatedInvites = prevInvites.filter(invite => invite.id !== inviteId);
        if (updatedInvites.length === 0) setShowPopup(false); // ÎßàÏßÄÎßâ Ï¥àÎåÄÎ©¥ ÌåùÏóÖ Îã´Í∏∞
        return updatedInvites;
      });

      refreshProjects(); // ÌîÑÎ°úÏ†ùÌä∏ Î™©Î°ù Í∞±Ïã†
    } catch (error) {
      console.error("‚ùå Ï¥àÎåÄ ÏàòÎùΩ Ïã§Ìå®:", error);
    }
  };

  // ‚úÖ Ï¥àÎåÄ Í±∞Ï†à Ï≤òÎ¶¨ (UI Ï¶âÏãú ÏóÖÎç∞Ïù¥Ìä∏)
  const handleRejectInvite = async (inviteId) => {
    const token = getAccessToken();
    try {
      await axios.post(`http://localhost:8082/api/team/invite/${inviteId}/reject`, {}, {
        headers: { Authorization: `Bearer ${token}` },
      });
      console.log(`‚ùå Ï¥àÎåÄ(${inviteId}) Í±∞Ï†à`);

      // ‚úÖ UIÏóêÏÑú Ï¥àÎåÄ Ï†úÍ±∞
      setInvites((prevInvites) => {
        const updatedInvites = prevInvites.filter(invite => invite.id !== inviteId);
        if (updatedInvites.length === 0) setShowPopup(false); // ÎßàÏßÄÎßâ Ï¥àÎåÄÎ©¥ ÌåùÏóÖ Îã´Í∏∞
        return updatedInvites;
      });

    } catch (error) {
      console.error("‚ùå Ï¥àÎåÄ Í±∞Ï†à Ïã§Ìå®:", error);
    }
  };

  return (
    <>
      {/* ‚úÖ Ï¥àÎåÄ ÌåùÏóÖ */}
      {showPopup && (
        <>
          <div className="invite-popup-overlay" onClick={() => setShowPopup(false)} /> {/* Î∞∞Í≤Ω Ïò§Î≤ÑÎ†àÏù¥ */}
          <div className="invite-list-container">
            <button className="close-popup-btn" onClick={() => setShowPopup(false)}>‚ùå</button>
            <h3>üì© Ï¥àÎåÄÎ∞õÏùÄ ÌîÑÎ°úÏ†ùÌä∏</h3>
            {invites.length === 0 ? (
              <p>üì≠ ÌòÑÏû¨ Ï¥àÎåÄÍ∞Ä ÏóÜÏäµÎãàÎã§.</p>
            ) : (
              invites.map((invite) => (
                <div key={invite.id} className="invite-card">
                  <p>
                    <strong>{invite.sender.username}</strong> ÎãòÏù¥ <strong>{invite.project.name}</strong> ÌîÑÎ°úÏ†ùÌä∏Ïóê Ï¥àÎåÄÌñàÏäµÎãàÎã§.
                  </p>
                  <div className="invite-buttons">
                    <button className="accept-btn" onClick={() => handleAcceptInvite(invite.id)}>‚úÖ ÏàòÎùΩ</button>
                    <button className="reject-btn" onClick={() => handleRejectInvite(invite.id)}>‚ùå Í±∞Ï†à</button>
                  </div>
                </div>
              ))
            )}
          </div>
        </>
      )}
    </>
  );
};

export default InviteList;
